INSERT INTO `unit_comments` VALUES (/*id*/1094011, /*unit_id*/109401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.3, /*change_face*/2, /*description*/"今日はあなたの誕生日なんだね\nその…おめでとう\nなにか欲しいものとかある？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094012, /*unit_id*/109431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー\nあなたのお祝いの日なのに、\n私までうれしいんだ。不思議だね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095001, /*unit_id*/109501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.6, /*change_face*/2, /*description*/"これが噂の着物ですね…\n見てよし、\n脱がせてよし、\n回してよしの魔性の服…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095002, /*unit_id*/109501, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/2.9, /*change_face*/6, /*description*/"あ～れ～～～～～！\nドＳさん、見ていたのですか！？\nクウカの回る姿を楽しんでいたの\nですね…やはり人格破綻者…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095003, /*unit_id*/109501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.8, /*change_face*/5, /*description*/"噂によると、着物では\n履かないものなのだとか…\nドッ、ドＳさん…確かめさせろ\nだなんていけませんよぉ…ぐふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095004, /*unit_id*/109501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/7.4, /*change_face*/5, /*description*/"あっ、帯が緩んでしまいました…\nドＳさん、締めなおしてくれるの\nですか？　…じゅるり。いい…\nいいですっ！　もっときつく～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095005, /*unit_id*/109501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.6, /*change_face*/5, /*description*/"ドＳさん…いえ、今はドS様と\n呼んだ方がいいでしょうか…\nクウカはドＳ様にお仕えする\n従順な…じゅるり", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095006, /*unit_id*/109531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この着物、露出が多くて\n少し恥ずかしいです…でも、\nドＳさんに見られていると思うと\nなんだか気持ちが…じゅるり", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095007, /*unit_id*/109531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あ～～～れ～～～～～！\nこの回転がクセになりそうです\nもっと、もっと回ります～！\nぐふ、ぐふふふ～～～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095008, /*unit_id*/109531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この衣装で踊ると、いつもより\nきれいに踊れている気がします…\n蝶のように舞うクウカを、\n蜘蛛のようにドＳさんが…ぐふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095009, /*unit_id*/109531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"「クウカの着物を剥ぎ取り、\nドＳさんは野獣のように…」\nぐふふ…はっ！　ドSさんに\n見られている気配がします…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095010, /*unit_id*/109531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この帯が気になるんですか？\nつ、つまりクウカを回したいと…！\nそして着物のはだけたクウカを\nそのまま美味しく…ぐふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095011, /*unit_id*/109501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.7, /*change_face*/5, /*description*/"今日は大切な日ですね…\nドＳさんが生まれた日…じゅるり\nつまりクウカの体でお祝いしろと\nいうのですね！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095012, /*unit_id*/109531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nところでドＳさん、和服の帯って\nラッピングしてるみたいですよね\nプレゼントは私、なんて…ぐふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096001, /*unit_id*/109601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.9, /*change_face*/2, /*description*/"オシノビでお出かけするなら\nいいところを知ってマス\n今度ワタシが案内するデス！\nきっと楽しいデスよー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096002, /*unit_id*/109601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.9, /*change_face*/6, /*description*/"もみじも好きデスけど\nもみじマンジュウも好きデスよ\n考えてたらお腹が空きまシタ…\nハナヨリダンゴデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096003, /*unit_id*/109601, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/8.6, /*change_face*/2, /*description*/"ヒトツ投げては天下統一のため…\nフタツ投げても天下統一のため…\nショーグンも手裏剣投げの特訓を\nしまセンか？　投げ放題デス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096004, /*unit_id*/109601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.0, /*change_face*/2, /*description*/"このキツネのお面はオーエド町の\n縁日で買いまシタ。歩くと鈴が\n鳴って、とてもかわいいデス\nコンコンデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096005, /*unit_id*/109601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"忍法・氷結地獄を思いつきまシタ\n冷たいカキ氷を\n敵に無理やり食べさせて\n頭をキーーーンとさせる技デス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096006, /*unit_id*/109631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンに仇名す敵に\n天誅デス！　天下統一するまで\n立ち止まりまセン！\n必殺忍法で勝ち進みマス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096007, /*unit_id*/109631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"フッフッフ…とっておきの\n新忍法の見せどころデース！\nムム、まだ使っちゃダメデスか？\nショーグンのイケズデス～…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096008, /*unit_id*/109631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンは天下泰平の世を\n築いてくれるでショウ！\nワタシはそんな城下町を\nずっとお守りいたソウロウ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096009, /*unit_id*/109631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ワタシのカチュヤク、しっかり\n見ていてクダサイ！　いつか\nチューシンと呼ばれるその日まで\nシロクジチュウおそばにいマス", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096010, /*unit_id*/109631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンのためならどんな\n敵とだって戦うデス！\nチューセーシンこそ天下統一の\n切り札だと信じていマス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096011, /*unit_id*/109601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.4, /*change_face*/1, /*description*/"誕生日おめでとうデス！\nプレゼントはショーグン専用の\nマイ手裏剣デース！\nいっぱい投げ込んでクダサーイ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096012, /*unit_id*/109631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はおめでとうゴザルデス！\n乾杯の音頭はワタシがとりマス！\nお手をハイシャクデース！\n朝までワッショイデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097001, /*unit_id*/109701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/"家事全般は得意ですので、\nなにかお役に\n立てることがありましたら、\nこのレムにお申し付けください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097002, /*unit_id*/109701, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.8, /*change_face*/2, /*description*/"姉様がメイドの仕事を\nしないで休憩している…ですか？\nさすが姉様！\nお休みする姿もすてきです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097003, /*unit_id*/109701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"鬼といっても、\n人間と大きく変わりませんよ\n魔獣を薙ぎ払ったり、\n鉄球を振り回せるぐらいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097004, /*unit_id*/109701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"身の回りの世話は\nこのレムにお任せください\n懸命にご奉仕\nさせていただきます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097005, /*unit_id*/109701, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.1, /*change_face*/2, /*description*/"こちらの世界では、\n角があっても\n珍しくはないのですね\nなんだか、少しうれしいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097006, /*unit_id*/109731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この鉄球ですか？\nこれは携帯用で、\n短くなっているから、\n普通の鉄球より軽いんですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097007, /*unit_id*/109731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"巨木であろうと、\n鬼であるレムを\n阻むことは不可能です\n全て粉砕させていただきます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097008, /*unit_id*/109731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この程度の巨木ならば、\nレムの力でどけられると思います\nあ、あの…上手くいったら\n褒めてくれてもいいんですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097009, /*unit_id*/109731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"女性にこの倒木を\n破壊するのは無理、ですか？\nご託は結構ですので、\nそこで見ていてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097010, /*unit_id*/109731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"強いって…\n女の子にその言葉は\nどうかと思いますよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097011, /*unit_id*/109701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.4, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nお祝いにケーキを焼きますね\nより豪華にするため、まずは\n飛竜の卵を調達してこなくては", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097012, /*unit_id*/109731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n今日のために色々と\n用意させていただきましたので、\n精いっぱいお祝いいたしますね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098001, /*unit_id*/109801, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.0, /*change_face*/3, /*description*/"ここが異世界。正直、\n信じられるものでもないけど…\nバルスの軽口よりは\nいくらかましかもしれないわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098002, /*unit_id*/109801, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/2.4, /*change_face*/3, /*description*/"面倒なことになったものね\nまぁ、危険人物はいないようだし\n楽できる分には問題ないわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098003, /*unit_id*/109801, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/1.9, /*change_face*/2, /*description*/"仕事ならしているわよ\n紅茶を優雅に飲むラムの姿が、\n周りの目を楽しませているわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098004, /*unit_id*/109801, /*use_type*/1, /*voice_id*/4, /*face_id*/3, /*change_time*/5.4, /*change_face*/4, /*description*/"これは風の系統魔法よ\nその気になれば、\n何でも細切れにできるわ\n疲れるから、やらないけど", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098005, /*unit_id*/109801, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/5.9, /*change_face*/2, /*description*/"蒸かし芋を食らうがいいわ\nおいしかったでしょう\n出来たて…いえ、\nふかしたてよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098006, /*unit_id*/109831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"千里眼をもつラムからは\n逃げられないわよ\n死にたくなければ、せいぜい\n知恵と勇気を振り絞ることね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098007, /*unit_id*/109831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"覚えておきなさい\n角をなくそうと、\n天才であるラムには\n遠くおよばないということを", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098008, /*unit_id*/109831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦闘では本当に役立たずね\n失望を隠すのが大変だわ\n…せいぜいラムの側から\n離れないことね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098009, /*unit_id*/109831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一気に決めさせてもらうわ\nなぜって？\nラムが…いえ、\n早く帰りたいからよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098010, /*unit_id*/109831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"風の魔法は便利よ。敵を\n切り裂くだけでなく、面倒な男を\nねじ切ることだってできるわ\nどこを？　ご想像にお任せするわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098011, /*unit_id*/109801, /*use_type*/2, /*voice_id*/6, /*face_id*/3, /*change_time*/6.5, /*change_face*/2, /*description*/"誕生日をラムに祝ってもらえる\nなんて光栄に思うことね\n誕生日プレゼント？　もちろん\n出来たての蒸かし芋よ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098012, /*unit_id*/109831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何が欲しいの？\n言ってごらんなさい\nなんでって、誕生日なんでしょ？\nご褒美くらいくれてやるわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099001, /*unit_id*/109901, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.9, /*change_face*/1, /*description*/"不思議なものね\nマナもあって精霊もいるのに\n見たこともないものがいっぱい\nここが異世界なんだ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099002, /*unit_id*/109901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"軽くお散歩でもする？\n精霊の子たちとお話ししにいくの\nそれがこの子たちとの\n契約条件の一つだから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099003, /*unit_id*/109901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.0, /*change_face*/6, /*description*/"さっき街に行ったら\nエルフの人をみかけたわ\nもしかしてこの世界にも\nハーフエルフっているのかな…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099004, /*unit_id*/109901, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.2, /*change_face*/6, /*description*/"コッコロちゃんって賢くて\nすごーくかわいいわよね\n…本当に私の妹だったら\nよかったのにな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099005, /*unit_id*/109901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.4, /*change_face*/6, /*description*/"こっちの世界に来てから\nパックってあまり出てこないの\nマナを使いすぎないように\n気を使ってるのかな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099006, /*unit_id*/109931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたも少し座らない？\nランドソルの花もすごーくきれい\n微精霊たちもよろこんでるわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099007, /*unit_id*/109931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少しお話しましょう？\nこの世界のことや\n私の世界のこと…\n話したいことがたくさんあるの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099008, /*unit_id*/109931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そっか、コッコロちゃんも\n精霊たちとお話しできるのね\nふふ、やっぱり他人とは\n思えないな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099009, /*unit_id*/109931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あら、花びらが髪についてる\nとってあげるから\n頭をこっちに向けて？\nそうそう…はい、とれたよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099010, /*unit_id*/109931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねえ…またこのお花畑を\n一緒に見に来てくれる？\n…精霊使いにとって約束は\n何よりも大切なんだからね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
