INSERT INTO `unit_comments` VALUES (/*id*/1108001, /*unit_id*/110801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"いいけど、\nなんかガッコーとかだと\nうち謎に恐がられてンだよね\nあんたは違うみたいだけどさ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1108002, /*unit_id*/110801, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.1, /*change_face*/1, /*description*/"ちょっと小耳に挟んだんだけど、\nあんたお金食べようとしたこと\nあんだって？　ウケる\nアレあんま美味しくないよね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
