INSERT INTO `unit_comments` VALUES (/*id*/1108005, /*unit_id*/110801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.1, /*change_face*/5, /*description*/"うわ、きも…\n何カッコつけてんの\n不覚にも一瞬キュンって\n来ちゃったじゃん…うち、きもっ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1108006, /*unit_id*/110831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜っていいよね。なんてゆか…\n色々見えすぎないカンジとか\n言えないこと言えちゃうノリとか\nあと…なんかカッコいいじゃん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
