INSERT INTO `unit_comments` VALUES (/*id*/1108008, /*unit_id*/110831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜に佇むミステリアスな少女…\nどーよコレ。カンジ出てない？\nただの夜勤休憩中なんだけどさ\nいいの、こういうのは気分だから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1108009, /*unit_id*/110831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あのさ。あんたのことだから\nべつに意味とか無いンだろうけど\n無言で人のカオ見んのやめて\nなんか謎にモジついて来るから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1108010, /*unit_id*/110831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたが？　送ってくれんの？\n何ソレ、誰が誰に言ってんのよ\n夜道は危ないって？　ウケる\nちょっとカッコいいじゃん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1108011, /*unit_id*/110801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.3, /*change_face*/5, /*description*/"あのさ。弟たちに作ったケーキ、\n余ってるから食べてくんない\nべつに無理にとは言わないけど\nあー…あと、おたおめ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1108012, /*unit_id*/110831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いいけど、あんた、\n今日が誕生日なんだって？\nおめでと。よかったね\nキラーペンシルあげるよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109001, /*unit_id*/110901, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/2.8, /*change_face*/1, /*description*/"なんか楽しいことないです？\n楽しいだけで生きていたい！\n楽しい為なら苦労もできる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109002, /*unit_id*/110901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/8.1, /*change_face*/6, /*description*/"せんぱーい！\n違うな…せ～んぱいっ！\nよし、こっちの方がかわいい\nあ、呼んだだけで用はないです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109003, /*unit_id*/110901, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.7, /*change_face*/1, /*description*/"え、先輩こんな簡単なことすら\nできないんですか？\nすごいな…すご…\nえっ、大丈夫ですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109004, /*unit_id*/110901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.9, /*change_face*/4, /*description*/"今更なんですけど先輩って\n見た目パッとしないですよね\nうーん…もうちょっと\n内面が滲み出るといいですね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109005, /*unit_id*/110901, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/11.6, /*change_face*/3, /*description*/"ちょっと聞きたいんですけど\nえっと、もしかしてアレです？\n先輩、私に気があったりします？\n違います？　違う？　違うのかよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109006, /*unit_id*/110931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"かわわなパフェと無敵の美少女！\nうーん映えるなー絵になるなー\nあ、先輩、悪いんですけど少し\n見切れてもらってていいです？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109007, /*unit_id*/110931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この後カラオケでも行きます？\nチエルが歌うんで、先輩には\nちーえるフゥ！　ちーえるフゥ！\nって跳ね散らかす係をあげますね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109008, /*unit_id*/110931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちぇる～ん♪　ちぇる　ちぇぱら\nちぇらるれろ　ちぇるぽぱっぴ？\nなーんて、嘘ですよー。えへっ☆\nいやあアオハルって感じですね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109009, /*unit_id*/110931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハイ、あーん？　…うわキモ\nなに本当に口開けてるんです、\n冗談に決まってるじゃないですか\n…もう、一口だけですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109010, /*unit_id*/110931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"先輩って、こんな顔のいい後輩と\n二人きりで何も思わないんです？\nいえ、それが先輩のいいとこです\nけど…え、まじ全然ないです？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109011, /*unit_id*/110901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.5, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n可愛い後輩から可愛い笑顔を\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1109012, /*unit_id*/110931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"先輩、ハピバ！\nプレゼントは何がいいです？\nちなみにチエルは可愛いアクセ、\n９月１５日生まれちゃんでーす！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110001, /*unit_id*/111001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/10.2, /*change_face*/2, /*description*/"気付き、学び、悟り\n先人の遺した書にも\n日々の生活にもそれはある\n共に高め合おうじゃないか、同志", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110002, /*unit_id*/111001, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/7.0, /*change_face*/4, /*description*/"この制服…袖や丈の余剰が\n気にならないと言えば嘘になる\n入学当時の成長見込みから、\n多少の下方修正が必要なようだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110003, /*unit_id*/111001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.7, /*change_face*/1, /*description*/"ぼくは本質的に文系なのでね、\n余白や行間を愛すのだよ\nゼロかイチかの理系思考に\nあまり浪漫は感じないのさ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110004, /*unit_id*/111001, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/5.4, /*change_face*/2, /*description*/"ふむ…そろそろ睡眠を\n取ろうと思っていたのだが、\n君が話相手になってくれるなら\n起きているのもやぶさかではない", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110005, /*unit_id*/111001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.8, /*change_face*/6, /*description*/"学びは最高の快楽だよ\nそれは脳を耕し、心を潤す\n差しあたっては…そうだな、\n君のことをもっと知りたい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110006, /*unit_id*/111031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"行儀が悪い？　なに、寝転がって\n読もうが背筋を正して読もうが、\n頭に入れてしまえば同じ知識さ\n君も隣りで横になりたまえよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110007, /*unit_id*/111031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"読書は孤独なスポーツだからな\n存外と体力を消耗するのだよ\nそこで君に腓腹筋のマッサージを\n依頼する。ほれほれ、揉みたまえ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110008, /*unit_id*/111031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あまりここから出たくはないが…\n信頼する君が請うなら仕方ない\nそこに学びがあると云うならば、\nぼくは書を閉じ、扉を開けよう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110009, /*unit_id*/111031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぼくにとって失いがたい学びは、\n君たちとの青春だ。世界の真理\nより大切なのさ。ことに生まれて\n初めて友達になってくれた君がね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1110011, /*unit_id*/111001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"生命とは実に尊い\n存在自体が億分の一の奇跡だ\nつまり端的に換言すれば、\n誕生日おめでとう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
