INSERT INTO `unit_comments` VALUES (/*id*/1111001, /*unit_id*/111101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.9, /*change_face*/6, /*description*/"ハロウィンですね…\nみんな仮装して、楽しそうです\n…怖いわけじゃありませんよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111002, /*unit_id*/111101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/6.0, /*change_face*/6, /*description*/"カボチャがいっぱいです…\nみんな顔があって、うぅ…\nな、なんで被ってるんですか！\nビックリさせないでください！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111003, /*unit_id*/111101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.7, /*change_face*/5, /*description*/"ど、どうでしょうか、この衣装…\n私は子供っぽいと思うんですけど\n…似合ってる？　そ、そうですか\nだったら…よかったです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111004, /*unit_id*/111101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.5, /*change_face*/6, /*description*/"あなたは仮装しないんですか？\nどんな衣装が似合うか、ですか？\nえっと…う～ん…吸血鬼さん？\nあ、血は吸わないでくださいね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111005, /*unit_id*/111101, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/6.0, /*change_face*/5, /*description*/"さっきからどうして\n頭を撫でてるんですか！\nかわいいから、って…\nもう！　もう～～～っ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111006, /*unit_id*/111131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お菓子、ありますか？\nない？　だったらイタズラですね\nか、噛みついちゃいますから！\nえ…えーいっ！　かぷっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111007, /*unit_id*/111131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は子供じゃないので言いません\n絶対に言いませんから！　え？\nお菓子、おいしいんですか？\n…トリック・オア・トリート…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111008, /*unit_id*/111131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、カップケーキ作ったんです\n食べますか？　え？\nイタズラの方がいい？\nへんた～い！　もうあげません！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111009, /*unit_id*/111131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハロウィン、\n一緒に…回りませんか？\n怖いんじゃありません！　もう、\nあなたはしょうがない人ですね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111010, /*unit_id*/111131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え？　お菓子、くれるんですか？\n……甘くて、おいしいです\nあ、手についちゃった…ぺろっ\nなっ、なに見てるんですかぁっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111011, /*unit_id*/111101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.1, /*change_face*/6, /*description*/"あなたのお誕生日…これ、どうぞ\n私が作ったカップケーキです\nあっ、そんなに急いで食べたら\nポロポロこぼしちゃいますよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1111012, /*unit_id*/111131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nこれ、パンプキンケーキです\nカボチャは少し苦手ですけど、\n頑張って味見もしたので…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
