INSERT INTO `unit_comments` VALUES (/*id*/1117006, /*unit_id*/111731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ク、クリスマスはサンタが\nプレゼントを贈る日で、\n今のわらわはサンタだから…\nと、とにかく受け取れ馬鹿者～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
