INSERT INTO `unit_comments` VALUES (/*id*/1118001, /*unit_id*/111801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"あけましておめでとうございます\n今年もたくさんおいしいものを\nいっぱい食べましょう！\n新年最初の約束です☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1118002, /*unit_id*/111801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"あなたはもう今年の抱負を\n決めていますか？\nわたしは…みんなが幸せに\n暮らせるような世界を、です", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1118003, /*unit_id*/111801, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.0, /*change_face*/2, /*description*/"わたしの着物、似合ってますか？\n髪もいつもと違うんです\nもっと近くで見てください\n特別ですよ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1118004, /*unit_id*/111801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/"あっ！　あっちで餅つきを\nやってるみたいですね！\n行きましょう！　自分でついた\nお餅は格別ですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
