INSERT INTO `unit_comments` VALUES (/*id*/1118006, /*unit_id*/111831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おせち、心を込めて作りました♪\n見た目はちょっとアレかも\nしれませんが、味は保証します！\nはい、どうぞ召し上がれ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
