INSERT INTO `unit_comments` VALUES (/*id*/1118009, /*unit_id*/111831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こうして料理を食べるのは\nいつものことですけど、お正月を\n一緒に過ごしてるって思うと\nなんだか照れちゃいますね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1118010, /*unit_id*/111831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"毎年こうやってあなたに\n手作りおせちを食べさせたいって\n思うのは…欲張りでしょうか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1118011, /*unit_id*/111801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.1, /*change_face*/1, /*description*/"誕生日おめでとうございます！\n縁起のいい食べ物をたくさん\n揃えたので一緒に食べましょう！\n魔物は…ヒミツです☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1118012, /*unit_id*/111831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n今年もこうしてあなたと過ごせて\n本当に幸せです☆\n来年もお祝いさせてくださいね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119001, /*unit_id*/111901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"主さま、新年あけまして\nおめでとうございます\n今年も主さまと一緒に\n歩めることを幸せに思います", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119002, /*unit_id*/111901, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/3.9, /*change_face*/1, /*description*/"まだまだ寒い日が続きますね\n主さま、お手手を繋ぎ\n暖めあって歩くといたしましょう\nぎゅっと握ってくださいまし", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119003, /*unit_id*/111901, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/8.0, /*change_face*/5, /*description*/"たまの里帰りもいいものですが\n主さまと離れるのは心細いです\n次の里帰りは一緒に、というのは\n欲張りでしょうか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119004, /*unit_id*/111901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.9, /*change_face*/1, /*description*/"甘酒には疲労回復の効果が\nあるとのことです。お疲れの際は\nわたくしがいつでもフーフーして\n飲ませて差し上げます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119005, /*unit_id*/111901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.7, /*change_face*/5, /*description*/"主さまにお仕えするという至高の\n初夢を見ることができました\n主さまの初夢にも、わたくしが\n出ていたならいいのですが…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119006, /*unit_id*/111931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一つ一つの動きに、主さまへの\n想いを込めております\n拙い舞ではございますが\n見守っていてくださいまし", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119007, /*unit_id*/111931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしの想いに反応して\n精霊たちも楽しそうに\n舞っておりますね。主さまも\nお楽しみいただけておりますか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119008, /*unit_id*/111931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"風情のある舞台で主さまと\n二人きりで過ごせる…\nわたくしにとって最高の\nお年玉でございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119009, /*unit_id*/111931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今年の抱負でございますか？\nそれはもちろんこれまで通り…\nいえ、これまで以上に主さまを\nお導きすることでございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119010, /*unit_id*/111931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまを想って舞っていると\n心も体も暖かくなります\nこの熱を主さまと分け合うことが\nできればいいのに…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1119011, /*unit_id*/111901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/5, /*description*/"お誕生日おめでとうございます\nもし…もし主さまがよろしければ\n二人きりで過ごすことを\nお許しいただきたく…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
