INSERT INTO `unit_comments` VALUES (/*id*/1120001, /*unit_id*/112001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"なに？　あたしと羽根つきで\n勝負したいの？\n手加減しないからね！\n墨まみれにしてあげるわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
