INSERT INTO `unit_comments` VALUES (/*id*/1129001, /*unit_id*/112901, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/8.7, /*change_face*/6, /*description*/"この服を着てると、自分が\n物語の主人公になったみたいです\n馬子も大爆笑、ってやつですね！\n…え？　何か間違えてましたか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
