INSERT INTO `unit_comments` VALUES (/*id*/1129004, /*unit_id*/112901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.6, /*change_face*/5, /*description*/"いつもと違うお洋服…\nお兄ちゃん、褒めてくれるかな？\nかわいいね、なんて言われたら\n私…ドキドキしちゃいますよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
