INSERT INTO `unit_comments` VALUES (/*id*/1130002, /*unit_id*/113001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.0, /*change_face*/6, /*description*/"…ウ、ウサギさんだぴょん♪\nなんちゃって…え？\nせ、先輩、いつからそこに…\n今のは忘れてくださいぃぃ…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130003, /*unit_id*/113001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/9.3, /*change_face*/5, /*description*/"ウ、ウサギさんは幸運の象徴\nですので、私が先輩のそばに\nずっと居れば…その…や、\nやっぱりなんでもないですぅ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130004, /*unit_id*/113001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"なんだかいつもより体が\n軽いような…これも、\nアリスちゃんの力が宿った、\nこの服のおかげかな♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130005, /*unit_id*/113001, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/11.7, /*change_face*/5, /*description*/"アリスちゃんの力で強くなった\n今の私なら、先輩を誘えるかも！\nせ、先輩！　一緒にご飯でも…\nや、やっぱりまだ無理ぃぃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130006, /*unit_id*/113031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ま、魔物と戦うのは怖いけど、\n私だって、\nアリスちゃんの力を受けた一人！\nやるときはやるんですぅ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130007, /*unit_id*/113031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"体が軽い…まるで、本物の\nウサギさんになったみたい…\nえへへ、今の私なら、\n先輩に褒めてもらえるかも♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130008, /*unit_id*/113031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩には\n指一本触れさせません！\n私が守りまひゅ…！？\nうう…またかんじゃったよぉ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130009, /*unit_id*/113031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩たちに\n頼ってばっかりじゃダメ！\n私だって頑張らないと！\nファイトよアユミぃぃぃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130010, /*unit_id*/113031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"時間を操作する力…\nこの力で、ずっと先輩を後ろから\n見て…じゃ、じゃなくてぇ！\nみんなの役に立ってみせますぅ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130011, /*unit_id*/113001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.0, /*change_face*/4, /*description*/"せ、先輩！！\nえっと…その、お、お誕生日、\nおめでとうごじゃいまひゅ！\nうう、またかんじゃったよぉ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130012, /*unit_id*/113031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\nこれ、プレゼントですぅ！\n…つ、ついに、\nかまないでちゃんと言えました♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131001, /*unit_id*/113101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.2, /*change_face*/2, /*description*/"風が通り抜けて風鈴が鳴ると\nああ、今年も夏が来たなぁ…\nって感じたりするじゃないか\nあの空気が、好きなんだよね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131002, /*unit_id*/113101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.6, /*change_face*/5, /*description*/"こういうヒラヒラした格好は\nあんまりしないんだよねぇ…\nいつもと勝手が違うから\nちょっと気恥ずかしいね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131003, /*unit_id*/113101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.9, /*change_face*/2, /*description*/"お前さん、スイカを食べるとき\n何かをかけたりする派かい？\nアタシはそのまま食べるのが\n好きなんだよね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131004, /*unit_id*/113101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"お前さん、夏の支度は万全かい？\nアタシはこの通りさ\n一緒にバカンスってやつを\n楽しむとしようか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131005, /*unit_id*/113101, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/4.2, /*change_face*/1, /*description*/"こう暑いと、かき氷が食べたいね\nお前さんも一緒にどうだい？\n色んな種類があるみたいだし\n別々の味を買おうじゃないか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131006, /*unit_id*/113131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"潮風を感じたり波音を聞いたり…\n普段は忙しくて、こんな時間\n取れたりしないからね\n夏を満喫しようじゃないか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131007, /*unit_id*/113131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もし急ぎの用事がないんなら\nここでゆっくりしていきなよ\n何をするわけでもないけど\nたまにはこういうのも、ね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131008, /*unit_id*/113131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おや、お前さん。散歩かい？\nアタシはのんびりしてたとこだよ\nきれいな海を眺めながら\nゆっくりするのは一番の贅沢さ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131009, /*unit_id*/113131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前さんと一緒にいるとさ\nなんていうか安心できるんだよね\n自然体の自分になれるというか\n肩肘を張らなくていいんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131010, /*unit_id*/113131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これからもお前さんと\n一緒に居られたらうれしいよ\n…ふふ、なんだいその顔は\n相変わらず読めないね、まったく", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131011, /*unit_id*/113101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"誕生日おめでとう\nあんまり大したものじゃないけど\nお祝いの品を用意しといたよ\nほら、受け取っておくれ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1131012, /*unit_id*/113131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はお前さんの誕生日だよね\nお祝いとして、アタシの手料理を\nたくさん用意しておいたんだよ\nさ、向こうで一緒に食べようか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132001, /*unit_id*/113201, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/5.4, /*change_face*/3, /*description*/"我が真名を勝手に晒しているとは\nなんという装束なのだ…\nこ、こら、シグルドよ！\nあまり胸元を見るんじゃない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132002, /*unit_id*/113201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"はーっはっはっは！\n我ら盟友の絆、この海でも\n変わることはないぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132003, /*unit_id*/113201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.4, /*change_face*/1, /*description*/"マフラーをしている理由？\nふっ、カッコいいからに\n決まっているではないか！\n他の理由などあるわけがない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132004, /*unit_id*/113201, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/13.1, /*change_face*/3, /*description*/"な、なぁ、シグルドよ\n貴様は私の水着を見てどう思う？\n…似合ってる？　いや、もっとこう\nかわいいとか…な、なんでもない", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132005, /*unit_id*/113201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.3, /*change_face*/1, /*description*/"日焼け止めはちゃんと塗ったか？\nもしまだ塗っていないなら\n私が貴様に塗ってやろう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132006, /*unit_id*/113231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルドよ、何をしているのだ？\n今こそ日光の力を集める機会\n我らの力を高めるためにも\nバカンスを満喫するのだ～っ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132007, /*unit_id*/113231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふふ。海に来たことで私は\n新たな力を得ることができた！\nこの強さを目に焼き付けるがいい\n行くぞ！　アターック！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132008, /*unit_id*/113231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"暑さが辛くないのか、だと？\nふっ。私を誰だと思っている…\n我が闇の力をもってすれば\n夏の日差しなど敵ではない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132009, /*unit_id*/113231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"最初は、この水着も\n恥ずかしいと思っていたのだが…\n貴様との絆の証だからな\n別の水着など考えられんな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132010, /*unit_id*/113231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルド、貴様と過ごす夏は\n最高に愉快だ！\n来年もまた来よう！\nその次もまた一緒だ、いいな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132011, /*unit_id*/113201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.8, /*change_face*/1, /*description*/"誕生日、おめでとう！\n今日は私から闇の祝福を与えよう\nこの力があれば貴様には様々な\n幸運が訪れる。感謝するがよい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1132012, /*unit_id*/113231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は貴様の誕生日か…\nよし、これをプレゼントしよう！\n私が考えた貴様の名乗り口上だ\nさぁ、今から練習をしてみるぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1133001, /*unit_id*/113301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/"どうどう？　この水着！\nいつもの魔法少女とは違う\n新たなナナカちゃんの魅力、\n君に教えてあげようじゃないか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1133002, /*unit_id*/113301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/"こういうダンサー風の\n魔法少女もいいでしょ？\nぐふふ、妄想が捗りますな～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1133003, /*unit_id*/113301, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/6.0, /*change_face*/1, /*description*/"ミステリアスな水着美少女\n彼女の正体は、敵か味方か…？\nみたいな登場しそうじゃない！？\n物語中盤で仲間になる系だよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
