INSERT INTO `unit_comments` VALUES (/*id*/1138010, /*unit_id*/113831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ア、アンタにはいつも助けられて\nばかりだから、アンタだけに\n特別なポーズ、見せてあげるわ\nこ、光栄に思いなさい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1138011, /*unit_id*/113801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.0, /*change_face*/6, /*description*/"ア、アンタの誕生日とかどうでも\nいいんだけど、一応お祝いは\nしてあげるわ\nエ、エンジェルとしてね！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1138012, /*unit_id*/113831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"た、誕生日おめでとう。今日は\nたまたまエンジェルの仕事が\n休みだから、私が祝ってあげるわ\nか、感謝しなさいよね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139001, /*unit_id*/113901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.7, /*change_face*/3, /*description*/"騎士さん、ハロウィンですね…\n少し付き合ってほしいんですけど\nかっ、勘違いしないでください！\n仕事の手伝いですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139002, /*unit_id*/113901, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.7, /*change_face*/1, /*description*/"なんですか、ジロジロ見て…\nもしかして騎士さんも仮装\nしたいんですか？　なら…\n仕方ないので作ってあげますよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139003, /*unit_id*/113901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.4, /*change_face*/2, /*description*/"それっ！　えーいっ！\n何してるのって、糸で縛る練習に\n決まってるじゃないですか\n標的はもちろん…分かりますね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139004, /*unit_id*/113901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/9.1, /*change_face*/5, /*description*/"ハロウィンと言えば仮装ですよね\nどうですか、私の衣装は？\n力作なのでどうぞ褒めてください\nかわいい？　…で、でしょうっ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139005, /*unit_id*/113901, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.4, /*change_face*/1, /*description*/"よし、お菓子の準備はオーケー♪\n騎士さんのことだから、お菓子を\n欲しがるに違いありませんからね\n手作りだけど…それは秘密です！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139006, /*unit_id*/113931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えっと、騎士さん\nどうせ暇ですよね？\nなら私と一緒にハロウィンを…\nもうっ、いいから行きますよっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
