INSERT INTO `unit_comments` VALUES (/*id*/1146011, /*unit_id*/114601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"今日はキミの誕生日だもの\nお祝いしないともったいないわ！\nそれじゃあ、かんぱ～い☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1146012, /*unit_id*/114631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日、おめでとう！\n後で二人きりでパーティー\nしちゃおっか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147001, /*unit_id*/114701, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"はーっはっはっは！\n新年の初笑いは悪党らしく\n盛大にだ！　オマエも一緒に\nやらないか？　気持ちいいぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147002, /*unit_id*/114701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.3, /*change_face*/3, /*description*/"悪党に休みなしだ！\n正月だろうとアタシは戦うぞ！\nさあ、文句があるヤツは\n全員かかってこい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147003, /*unit_id*/114701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.8, /*change_face*/8, /*description*/"一年の計はなんとかっていうだろ\nだからアタシは、悪党らしく\n正月から悪いことをするぞ！\nま、年中悪さしてるけどな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147004, /*unit_id*/114701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/8.3, /*change_face*/6, /*description*/"出店がいっぱいだな！　これじゃ\nお年玉がいくらあっても\n足りないぞ！　なあ、オマエのは\nまだ…な、なんでもない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147005, /*unit_id*/114701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/"まさかオマエと新年を\n過ごすことになるなんて…\nでも悪い気はしないな！\nなんか美味いもんでも食うぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147006, /*unit_id*/114731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシが天楼覇断剣で\n全部真っ二つにしてやる！\n空だろうが海だろうが\n知ったことか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147007, /*unit_id*/114731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"飾りをつけた天楼覇断剣も\n意外と悪くないぞ！\nぶった切れればなんだって\nいいんだけどな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147008, /*unit_id*/114731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"気持ちのいい景色だな！\nどこかでアイツも…\nオクトーも、こんな風に\n日の出を見てるのかな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147009, /*unit_id*/114731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今年もオマエにはいい事あるぞ！\nなんてったってアタシと\nこの天楼覇断剣が一緒だからな！\nどんな未来も切り拓いてやる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147010, /*unit_id*/114731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"プリンセスナイトの力って\n初日の出に似てると思わないか？\n温かくて、柔らかくて…それに\nなんか力がみなぎってくるだろ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147011, /*unit_id*/114701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"誕生日おめでとう！\nオマエのお祝いができて\nアタシもうれしいぞ！\nもっともっと祝わせろ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147012, /*unit_id*/114731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n今日はオマエの言う事を\nなんでも聞いてやるぞ！\nアタシに遠慮はするなよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150001, /*unit_id*/115001, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/6.1, /*change_face*/2, /*description*/"この私を待たせるとは\n良い度胸をしていますね\n…冗談ですよ\n待ち合わせ時刻、丁度です", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150002, /*unit_id*/115001, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"正月の街は騒々しいですね…\nですが、これはこれで\n観測するデータには\n事欠かないとも言えますか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150003, /*unit_id*/115001, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"せっかくの正月です\n貴方も人の事ばかりにかまけず\n自分のための時間を持ちなさい\nお姉さんからのアドバイスですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150004, /*unit_id*/115001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.1, /*change_face*/6, /*description*/"これを飲みなさい\nそこで売っていた甘酒です\n貴方に体調を崩されては\n私としても困りますから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150005, /*unit_id*/115001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/9.8, /*change_face*/2, /*description*/"年下に見えるお姉さんは\n好きですか？\nええ、貴方の好みを聞いていますが\n…そう。それなら良いのです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150006, /*unit_id*/115031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あけましておめでとうございます\n今年も私の様々な姿を\n貴方に見せてあげますよ…\nそのままがいい？　…そうですか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150007, /*unit_id*/115031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"雪…こうして触れてみると\n儚いものなのですね\nあっという間に形が変わる…\n少し、親近感を覚えます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150008, /*unit_id*/115031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"舞い散る雪を眺めながら\n温かい甘酒を飲むというのも\nなかなか乙なものですね\n貴方も付き合いなさい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150009, /*unit_id*/115031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"外出など普段はしないのですが…\n気が向いたので初詣に行きますよ\n…ええ、他意など一切ない\n単なる気まぐれです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150010, /*unit_id*/115031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"女性の晴れ姿を見た際には、まず\n言うべき事があると思いますが？\n…ふふ、よくできました\nもっと褒めてもいいのですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150011, /*unit_id*/115001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"今日は貴方の誕生日ですね\n私の独断で贈り物を用意しました\nありがたく受け取りなさい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1150012, /*unit_id*/115031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとうございます\nまた一つ成長した貴方を\n見せてくれることを\n期待していますよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155001, /*unit_id*/115501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.2, /*change_face*/2, /*description*/"どんな困難が行先を阻もうとも\nわたくしはこの手で明日を\n掴んでみせます。わたくしの\n決意を見ていてくださいまし", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155002, /*unit_id*/115501, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"主さまのおそばにいられる幸せは\n当たり前のものではなく\nとても特別なことなのだと\n感じておりますよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155003, /*unit_id*/115501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.7, /*change_face*/2, /*description*/"主さま、なにかお困りのことは\nございませんか？　これからも\n変わらず、わたくしになんなりと\nお申し付けくださいまし♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155004, /*unit_id*/115501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"この身に新たな力を宿せたのは\n全て主さまのおかげでございます\n恩返しできるよう、一生をかけて\nガイドさせていただきます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155005, /*unit_id*/115501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/8.8, /*change_face*/5, /*description*/"主さまをお慕いする気持ちには\nこの真っ白な装束と同じく\n一切の曇りもございません\nわたくしの全ては、主さまに…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155006, /*unit_id*/115531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしは独りではございません\nかけがえのない仲間のため…\n絆を紡いだ主さまのために\nこの身の全てを捧げます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155007, /*unit_id*/115531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"精霊たちが、わたくしたちの絆を\n祝福してくれているようです\n笑顔あふれる素晴らしい仲間に\n心からの加護を！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155008, /*unit_id*/115531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"猛き風の精霊よ…\n未来に向かって歩まんとする\nわたくしたちの背中に\n祝福の追い風を！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155009, /*unit_id*/115531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"絆を繋ぎとめてくださった\n主さまの優しいお手手…\nその温もりは、この胸に灯る\n希望の明かりでございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155010, /*unit_id*/115531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまの優しくて\n耳をくすぐるそのお声で\nどうかこれからもわたくしの名を\n呼んでくださいまし♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155011, /*unit_id*/115501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今日は主さまがお生まれになった\nとてもおめでたい日ですね\n精霊たちとともに、心を込めて\nお祝いをさせてくださいまし♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1155012, /*unit_id*/115531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nまた一つ大人になられましたね\n未来に向かって歩むその隣で\nこれからもわたくしはずっと…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156001, /*unit_id*/115601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/9.0, /*change_face*/7, /*description*/"精霊の意思を感じ取る…\nやっぱり難しいなぁ…\n騎士クンもやってみる？\n…あれ？　寝ちゃった！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156002, /*unit_id*/115601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/8.4, /*change_face*/7, /*description*/"精霊って、色んな子がいるんだね\n優しい子だけじゃなくて、\nいたずら好きな子も…ふにゅ！\nうぅ～…ほっぺ突っつかないで～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156003, /*unit_id*/115601, /*use_type*/1, /*voice_id*/3, /*face_id*/7, /*change_time*/9.6, /*change_face*/8, /*description*/"この服、刺激が強くないかな…？\nだってほら、こんなに布が\n少なくて…はっ！\nあんまり近くで見ないで～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156004, /*unit_id*/115601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/13.3, /*change_face*/6, /*description*/"精霊を使役してる時って\n感覚が普段より冴えるの\n今も風や光の変化を感じるよ…\n騎士クンは…お腹減ってきた？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156005, /*unit_id*/115601, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/7.4, /*change_face*/7, /*description*/"…うん、騎士クンとの距離が\n中々縮まらな…え、騎士クン！？\nこ、これはその、精霊と話を\nしてて…い、今すぐ忘れて～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156006, /*unit_id*/115631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン…この子を一緒に\n育ててくれて、ありがとう\nわたしね、とってもうれしかった", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156007, /*unit_id*/115631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大丈夫…伝わってるよ\nあなたの思いも、あなたの力も…\nずっと元気でいてね。約束だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156008, /*unit_id*/115631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンのためなら、\nいつでも呼んで…だって\nこの子もわたしも、\n同じ気持ちだよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156009, /*unit_id*/115631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お父さん…ちがっ！　違うの！\nこの子が今、騎士クンのこと\nそう呼んでて…もぉ～！\nさっきのは忘れて～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156010, /*unit_id*/115631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"人と精霊の気持ちが通じ合うなら\nこの気持ちも、騎士クンに\n通じたらいいのにな…あれ…？\n今、何も言ってない…よね…！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156011, /*unit_id*/115601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/"お誕生日おめでとう、騎士クン！\n精霊もおめでとうだって\nふふっ、やっぱり騎士クンは\nみんなに慕われてるんだね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1156012, /*unit_id*/115631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとう！\nあ、あのね？　精霊から\n祝いの舞を教わったの\n踊ってみても…いい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157001, /*unit_id*/115701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.3, /*change_face*/6, /*description*/"助手くん、準備はできたかい？\n今度の事件現場は海だよ！\n二人で一緒にバカンス\n…じゃなかった、調査に出発だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157002, /*unit_id*/115701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.1, /*change_face*/6, /*description*/"海に持っていくものといえば\n手帳にルーペ、望遠鏡…\nあとは浮き輪にサーフボード…\nあれ…なにをしに行くんだっけ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157003, /*unit_id*/115701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"海か…みんなに聞かれたら\nついていくって言いだしそうだ…\nだから、内緒にしておこうね？\n私と君だけの秘密、だよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157004, /*unit_id*/115701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.4, /*change_face*/5, /*description*/"この髪型、動きやすくていいね\n…助手くんも気に入ってくれた？\nじゃ、じゃあしばらくはこれで…\n深い意味はないんだよっ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157005, /*unit_id*/115701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.9, /*change_face*/5, /*description*/"事件、すぐに解決できるといいね\nえっ？　なんでって…それは…\n…助手くんと遊びたいからだよ！\nそれくらい見抜きたまえ、もう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157006, /*unit_id*/115731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くんっ、見たまえ、海だよ！\nなんだい？　その驚いた眼差しは\n私だってはしゃぎたいときもある\n…助手くんと海に来たときとかね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157007, /*unit_id*/115731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"波打ち際の名探偵…\nう～ん、いい響きだ♪\nさぁ助手くん、青き水平線に\n隠されし真実、見つけに行こう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157008, /*unit_id*/115731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くん、君の観察力を試そう\n私は今、普段と違う格好だろう？\nなにか気づいたこととか…えっ？\n水着、似合ってる？　えへへ…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157009, /*unit_id*/115731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"世界にはまだまだ謎があふれてる\nこうして色々なところを巡って\n真実を見つけるのも…いいかもね\nもちろん助手くんと一緒に、だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157010, /*unit_id*/115731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつも私は君のことを助手くんと\n呼んでいるだろう？　でも今は、\n遊びに来ているわけだから…\nその…やっぱりなんでもないっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157011, /*unit_id*/115701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"さて助手くん、\n呼ばれた理由は分かるかな？\nふふ、私にはお見通しなのさ…！\n誕生日、おめでとうっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1157012, /*unit_id*/115731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くん、誕生日だよね\nこれを受け取って欲しい\nこのプレゼントに込められた意味\n…解き明かしてみたまえ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1158001, /*unit_id*/115801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/"まさか私が貴族様の舞踏会に\n出られるなんて…きちんと\nレッスンがんばらなきゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1158002, /*unit_id*/115801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"人間の姿になれるだけでも\nすっごく幸せなのに、こんなに\nすてきなドレスを着られるなんて\n本当に夢みたいだわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1158003, /*unit_id*/115801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/4.4, /*change_face*/1, /*description*/"淑女ってどういう人のことを\nいうのかしら…森で木を切る\n淑女がいてもいいわよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1158004, /*unit_id*/115801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.6, /*change_face*/2, /*description*/"牧場での暮らしは本当に幸せで\nこれ以上を望む必要なんて\nないけど、やっぱり華やかな\n世界も憧れちゃうわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1158005, /*unit_id*/115801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.7, /*change_face*/2, /*description*/"ドレス姿が似合ってる？\nふふ、ありがとう。あなたに\nそう言ってもらえれば\n安心だわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
