INSERT INTO `unit_comments` VALUES (/*id*/1158011, /*unit_id*/115801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.3, /*change_face*/1, /*description*/"お誕生日おめでとう！\nあなたのために森で花を摘んで\nきたんだけど、受け取って\nもらえるかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
