INSERT INTO `unit_comments` VALUES (/*id*/1160012, /*unit_id*/116031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は王子はんのお誕生日やね！\nそういうわけで…じゃーん！\nお花のシャワーや！\nふふっ、きれいやろう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162001, /*unit_id*/116201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.0, /*change_face*/4, /*description*/"テレフェスっていうか、\nまあ体育とかスポーツ的なのは\n得意な部類だよね、個人競技は\n団体競技は…協調性が課題だわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162002, /*unit_id*/116201, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/0.0, /*change_face*/6, /*description*/"いいけどユニ先輩のフェス服、\nアレ何なの。似合ってるけど\nチエルのも無駄にデコってて、\nアレ何なの。似合ってるけど", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162003, /*unit_id*/116201, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.1, /*change_face*/6, /*description*/"いんだけどさ、最近テレ女の空気\n全体的にちょっとアレじゃない？\nや、なんか校風崩れっていうの？\nルーセンティズム入ってきてね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162004, /*unit_id*/116201, /*use_type*/1, /*voice_id*/4, /*face_id*/8, /*change_time*/10.1, /*change_face*/6, /*description*/"そういやあんたもどっかの学校で\nスポーツ経験あるらしいね\nそうなんだ…へー、いいじゃん\nえ、海辺で野球？　何それ冗談？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162005, /*unit_id*/116201, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/8.5, /*change_face*/1, /*description*/"え、ちょ何…ガン見やめてよ\nわりかし肌出てンの思いだして、\n急にモジついて来ちゃうじゃん…\nあ？　寒そう？　いいえ、別に", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162006, /*unit_id*/116231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よっ、と楽勝…！\nつーかさ、障害物競走なんて、\nフツー子供のやるもんでしょ…\nマジでこの学校アタオカだわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162007, /*unit_id*/116231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちょー、いつもの外野うるさい\nハズめの声援やめてくんない\nあと、うち不良ではないから\nいや毒婦でもねーわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162008, /*unit_id*/116231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そんな、うちの応援とかいいから\nあんたも自分の競技がんばりー？\n次なに出んの。あ？　二人三脚？\nえ、誰と。別にいんだけど、誰と", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162009, /*unit_id*/116231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんか悪いね、あんたに弟の\n面倒まで見てもらっちゃって…\nそだ、このあと家族とおべんと\n食べんだけど良ければ一緒にど？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162010, /*unit_id*/116231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハ？　いや、格好いいとかそんな\n真顔で言うフツー？　はっず\nや、べつに言うなってわけじゃ…\n言いたきゃそれは…言いなよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162011, /*unit_id*/116201, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/7.7, /*change_face*/1, /*description*/"あんた今日、誕生日だって？\nなんで言わんの、そーゆーこと…\nなんも用意してなかったじゃん\nじゃあ飴ちゃん多めにあげるわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1162012, /*unit_id*/116231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんた今日、誕生日だよね\nああいや、さっきチエルに聞いて\nそだ、偶然ケーキ作ってきたから\nコレあげるわ。いや偶然だけど", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163001, /*unit_id*/116301, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/4.1, /*change_face*/3, /*description*/"先輩、大変です！\n若者のちぇる離れが深刻です！\nちぇる語普及委員の一人として\nどうお考えなんですか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163002, /*unit_id*/116301, /*use_type*/1, /*voice_id*/2, /*face_id*/7, /*change_time*/6.8, /*change_face*/1, /*description*/"先輩、大変です！　今ならなんと\nこの「幸せになれるちぇる」が\n１万ルピでお手元に。どうです？\nちぇるは肉眼では見えませんけど", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163003, /*unit_id*/116301, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/8.3, /*change_face*/4, /*description*/"クロエ先輩がチエルのフェス姿\n可愛いって言ってくれないんです\n…え、先輩の前では言ってた？\nやだー！　直接言ってほしいー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163004, /*unit_id*/116301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/10.4, /*change_face*/6, /*description*/"そういえば先輩もどっかの学校で\nスポーツ経験あるらしいですね\nそうなんだ…ふうん意外な一面…\nえ、海辺で野球？　何それ冗談？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163005, /*unit_id*/116301, /*use_type*/1, /*voice_id*/5, /*face_id*/7, /*change_time*/5.4, /*change_face*/2, /*description*/"ちぇる～ん☆　どうですか、この\nチエル仕様のフェス用ウェア！\nえっ？　そんなに似合ってます？\nですよね、知ってます。えへへ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163006, /*unit_id*/116331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちぇる～ん！　ちぇる　ちぇろぱ\nちぇるぴぴぱ　ちぇぴらぺぽー？\nうんうん、だよねーおっけー！\nじゃあリズムを上げて行くよー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163007, /*unit_id*/116331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなー！\nちえるん単推しなのは分かるけど\n後ろの地味な二人にも\n少しは歓声送ってあげてねー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163008, /*unit_id*/116331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やっぱりフェスの最後はお約束、\nヒロインたちのライブだよねー！\n会場のみんなも配信のみんなも…\nえ、これ魔石配信してないの？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163009, /*unit_id*/116331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちぇるミナすこすこ侍の先輩も\nファンサで釣ってあげますよー！\nそうれ、投げキッＣＨＵ～☆\nって、何よそ見してるんですか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163010, /*unit_id*/116331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"歌って踊れるスーパーアイドル、\nこれが世界の美少女ちえるんです\nどうです先輩、惚れ直しました？\nえ、真顔で頷かれても…困る…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163011, /*unit_id*/116301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.3, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n可愛い後輩から可愛いちぇるを\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1163012, /*unit_id*/116331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"先輩、ハピバ～！\nじゃあ今日は特別なんでチエルが\n一緒にケーキ食べてあげますね！\nお祝いだから割り勘でいいですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164001, /*unit_id*/116401, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/8.7, /*change_face*/1, /*description*/"聖学祭か…気が重いな…\n学芸発表はともかく運動競技は…\nうむ、人にはそれぞれ適性がある\n荒事は荒くれ後輩たちに任せよう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164002, /*unit_id*/116401, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.8, /*change_face*/1, /*description*/"聖学祭に提出したぼくの芸術は、\nあまり評価を得なかったらしい\nぼくは印象派だからな、\n心得のない者には分からぬ領域だ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164003, /*unit_id*/116401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/10.6, /*change_face*/2, /*description*/"高機動万能靴『神速くん』\nこれはからくり工学の成果物を、\nぼくが魔法で再解釈したものだ\n理系不要論の物証なのだよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164004, /*unit_id*/116401, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/10.7, /*change_face*/4, /*description*/"ときに後輩、君はどこぞの学院で\nスポーツを学んできたらしいな\nそれはどういった類の…\nなに、海で野球？　難しいな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164005, /*unit_id*/116401, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/10.5, /*change_face*/6, /*description*/"君と二人で撮った写真だが、\n塔の書庫の深くに保管してある\n他人に見せるのは躊躇われてな\n何なのだろうか、この情動は…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164006, /*unit_id*/116431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"年に一度の文武の祭典…\n皆が祭囃子に浮足立つ日こそ、\n賢者はあえて日常を貫くのだよ\nではロゼッタ、後はよしなに…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164007, /*unit_id*/116431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これは決してゴロ寝ではない\n体力温存と精神集中を兼ねた\n革新的なトレーニング法なのだ\n端的に換言すれば、ゴロ寝だ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164008, /*unit_id*/116431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぽーん！　子守唄を再生します！\n♪ユニちゃん良い子だ眠りゃんせ\n♪夢の扉で真理に接続\n♪多次元宇宙で神を見な～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164009, /*unit_id*/116431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なに、ぼくの運動不足が心配？\nそうか、よくぞ言ってくれた\nでは君に下肢部のストレッチを\n依頼する。ほれほれ、解したまえ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164010, /*unit_id*/116431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やあ同志、君も午睡に来たのか\n遠慮せず横になりたまえよ\nロゼッタの膝枕は売約済みだが、\nぼくので良ければ貸してやろう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164011, /*unit_id*/116401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"おい同志、ぼくのメモによれば\n今日は君の誕生日じゃあないか\n祝いにぼくの講義を授けよう\n席につきたまえ、夜は長いぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1164012, /*unit_id*/116431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちぇる～ん♪　後輩ハピバ！\nぼくは都の風習に疎いゆえ、\nチエル君に祝い方を聞いてきた\nどうだ愛くるしいだろう。にこり☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165001, /*unit_id*/116501, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"はーっはっはっは！\nビッグボス・イノリ様の登場です\nお兄さんは今日から側近として\nあたしをサポートするですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165002, /*unit_id*/116501, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/10.4, /*change_face*/3, /*description*/"あたしの知名度が広まるよう\nお兄さんはあたしを担いで…え？\n肩車すればいいのかって…\nそういう意味じゃねーですよ！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165003, /*unit_id*/116501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"あたしがランドソルで一番の\nボスになったら、カルミナを\n呼んでパーティーです！\nお兄さんも呼んであげますから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165004, /*unit_id*/116501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/11.0, /*change_face*/3, /*description*/"このポーズとこのポーズ\nどっちがかっこいいですか！？\n…どっちもかわいい？\nガ、ガキ扱いするなですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165005, /*unit_id*/116501, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/7.9, /*change_face*/3, /*description*/"お兄さんの仕事は\nそのお人好し力を\nあたしのために使うことです！\nよそ見は禁止ですよ！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165006, /*unit_id*/116531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですか、このポーズ！\n…え？　台の上はあぶない？\nお兄さんは分かってないですね\n高い所に登るのがボスの鉄則です", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165007, /*unit_id*/116531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄さんには見えますか！\n空に輝くあの一番星が！\nあれこそギャングスターです！\n今は昼？　心の目で見るですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165008, /*unit_id*/116531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしがボスになったら\nイノリのお菓子工場を作らせます\nお小遣いとお菓子の両取りですよ\nくぅ〜！　夢が広がるです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165009, /*unit_id*/116531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの計画ではお兄さんは側近\nカヤぴぃは右腕にして、ふふっ\nボスは雑用係にしてやるですよ！\nぴっ！？　今寒気がしたような…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165010, /*unit_id*/116531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっ、この服どうですか？\nかっこいいって言っても…\nはぁ！？　か、かわいいって\n言うのは、やめやがれですー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165011, /*unit_id*/116501, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/2.9, /*change_face*/2, /*description*/"今日はお兄さんの誕生日？\n一億年前から知ってましたよ！\nタコスおごるので、お兄さんは\nあたしにタコスおごるですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1165012, /*unit_id*/116531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうです！\nあたしは部下想いのボスですから\nわがままを聞いてあげなくも…\nってなんですその本気の目は！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166001, /*unit_id*/116601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/6.4, /*change_face*/1, /*description*/"全く、喧嘩屋のオレに\nこんな格好までさせやがって\nまあ、やるからにはキッチリ\nやるさ。半端なのは嫌いだからな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166002, /*unit_id*/116601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.5, /*change_face*/6, /*description*/"この格好、\n案外動きやすいんだよな\nこれならケンカも…\nや、やらねえよ！　…多分", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166003, /*unit_id*/116601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.9, /*change_face*/6, /*description*/"本日はご利用いただき\nありがとうございまーす！\n…自分で言うのもなんだけど\n全然似合わねーな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166004, /*unit_id*/116601, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/13.5, /*change_face*/6, /*description*/"この仕事は笑顔が大事\n分かっちゃいるけど難しいぜ…\nに、にこぉ…って\n何見てんだよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166005, /*unit_id*/116601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"キミがついてきてくれてると\nなんだか一緒に旅してる\nみたいな気がする\n結構楽しいもんだな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166006, /*unit_id*/116631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よく来たな。人気スポットから\n危険地帯まで、どんなトコでも\nガイドしてやるよ\nさあ、たぎるような旅に出発だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166007, /*unit_id*/116631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"現地でトラブルがあったら\n迷わずオレを呼びな！\nランドソルで一番荒事に強い\nガイドだからな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166008, /*unit_id*/116631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミの頼みならどんな危険な\n場所だって連れてってやるよ\nドラゴン族渾身のガイドで\n存分に楽しませてやるからな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166009, /*unit_id*/116631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミがいなけりゃ、この仕事も\nこんなに上手くいかなかった\nだろうな。やっぱダチってヤツは\n最高だぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166010, /*unit_id*/116631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なあ、いつか二人で\n旅に行こうぜ！　仕事とか\n関係なく、好きなとこ行くんだ\nキミとならきっと楽しいだろうな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166011, /*unit_id*/116601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.0, /*change_face*/2, /*description*/"今日誕生日だろ？\nほら、プレゼントだ\nこの前土産物屋に行った時に\nいいなって思ってさ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1166012, /*unit_id*/116631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よう、誕生日おめでとう！\n今日はオレが厳選した店に\n連れてってやるよ。年に一度の\n大事な日だ、パーッと祝おうぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167001, /*unit_id*/116701, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/5.4, /*change_face*/2, /*description*/"…よし！　…よし！　…よし！\nうん、発声練習終わり…！\n今日も一日、ベルトコンベアと\nにらめっこです…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167002, /*unit_id*/116701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"日が昇る前から\n工場の前に立ってますよ…！\n無遅刻無欠勤を目指します！\nえっへん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167003, /*unit_id*/116701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.7, /*change_face*/2, /*description*/"ランチは工場の先輩から\n誘われるまで倉庫の隅っこで…\nあなたも一緒にどうですか…？\n静かで過ごしやすいですよ…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167004, /*unit_id*/116701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"工作の技術は\nマイフレンドくん作りで\n身に付けました…！\n工場でも通用するといいなあ…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167005, /*unit_id*/116701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.8, /*change_face*/6, /*description*/"うう…人生初めてのアルバイト…\n不安で消えてなくなりそうです…\nで、でも…あなたも一緒だから\nきっと大丈夫ですよね…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167006, /*unit_id*/116731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『だいじょぶマイフレンドくん\nＶ４』のカスタマイズも…\nバイト仲間を作ってぼっち卒業も\nこのバイトで叶えてみせます…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167007, /*unit_id*/116731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぼっちの私に、誰かと一緒に\n作業する日が来るなんて…！\nまさか夢…！？\n絶対そうに決まってます〜！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167008, /*unit_id*/116731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"バイト先でもＢＢ団の使命は\n忘れませんよ…！\nぼっち卒業を目指して\nバイト仲間作りにも励みます…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167009, /*unit_id*/116731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"工具や機械の使い方も\n慣れてきました…！\nけど、バイト先の人との会話は\n全然慣れません…ううっ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167010, /*unit_id*/116731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ＢＢ団でそろって\n一緒にバイトができるなんて…！\nおもちゃも、バイト仲間も\n作りましょうね…えへへ…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167011, /*unit_id*/116701, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/7.9, /*change_face*/2, /*description*/"あなたのお誕生日を祝う言葉を\nわ、私が…！？　し、幸せすぎて\nちょっと怖いですが…せーのっ\nお誕生日おめでとうございます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1167012, /*unit_id*/116731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたのお誕生日をお祝い\nさせてください！　歌に、花束の\n贈呈…それと100通の手紙です\n…では心を込めて歌います！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168001, /*unit_id*/116801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"たい焼きいかがかにゃ～！\n…あ、キミいいところに！\n手伝ってくれたら特別に\nたい焼きの賄いをあげるにゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168002, /*unit_id*/116801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.8, /*change_face*/1, /*description*/"にゃはは♪\nこのたい焼きメーカーがあれば\n売り上げ爆増だにゃ！\n貯金箱が貯まっていくにゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168003, /*unit_id*/116801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/"お客さんが美味しそうに\nたい焼きを食べてるにゃ！\nやっぱりたい焼きは\nみんなを幸せにするにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168004, /*unit_id*/116801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.0, /*change_face*/1, /*description*/"キミ、じっとあたしを見てるけど\nこの服装気に入ったにゃ？\nなんなら…特別価格で\nポーズしてあげるにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168005, /*unit_id*/116801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"忙しくても笑顔は忘れにゃい！\n商売人の鉄則にゃ！\nほら、キミも笑顔にゃ！\n…うん、その方が男前にゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168006, /*unit_id*/116831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"忙しくても大丈夫にゃ♪\n猫の手に代わって\n自動たい焼きメーカーが\nあたしをサポートしてくれるにゃ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168007, /*unit_id*/116831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たい焼きを買ってくれたお礼に\nスマイルもサービスするにゃ！\nウェイトレスのバイトも\n出来そうな気がするにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1168008, /*unit_id*/116831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たい焼き屋の商売が忙しくても\n義賊活動でピンチになっても\n背中を預ける人がいれば平気にゃ\nこれからもよろしくにゃ、相棒！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
