INSERT INTO `unit_comments` VALUES (/*id*/1169004, /*unit_id*/116901, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/"キミを一人前に鍛えるのも\nバイトリーダーのお仕事だもの\nわからないことがあれば\nすぐに聞いてちょうだいね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
