INSERT INTO `unit_comments` VALUES (/*id*/1169006, /*unit_id*/116931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"リーダーとして\nキミを導いてみせるわ！\nもちろん、効率全開で！\nさあ、準備はいい！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1169007, /*unit_id*/116931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日の製造ノルマも\n順調に消化中…いい感じね！\nさあ、このまま最大効率で\n走り抜けるわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1169008, /*unit_id*/116931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アオイちゃんもタマキさんも\nよく働いてくれて助かるわ\nキミも一緒なら鬼に金棒よ！\nどんなノルマもどんとこーい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1169009, /*unit_id*/116931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お仕事が終わったら\n夕飯でも一緒にどう？\n近場の安くておいしい\nお店、見つけたのよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
