INSERT INTO `unit_comments` VALUES (/*id*/1169011, /*unit_id*/116901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"誕生日おめでとう！\nまた一歩大人になったわね\nキミの成長をそばで見てると\n私も師匠として鼻が高いわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
