INSERT INTO `unit_comments` VALUES (/*id*/1170001, /*unit_id*/117001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.1, /*change_face*/5, /*description*/"真夏の日差しのもと、あなた様の\nすべてを手に入れます…\n運命の伴侶として…\n永遠に…離しませんわ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170002, /*unit_id*/117001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"私たちの間を引き裂くものは\nすべて破壊しつくしますわ…\nそう、海の藻屑と消えるまで\n細かく細かく…念入りに…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170003, /*unit_id*/117001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.4, /*change_face*/2, /*description*/"この水着、似合いますか？\n…えぇ、いいお返事…\nあなた様との最高の夏のために\n最高のものを選びましたわ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170004, /*unit_id*/117001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/9.9, /*change_face*/2, /*description*/"海で水着くらい当然ですわ…\nでもあなた様に褒めていただける\nのは純粋にうれしいもの\nクスクス…もっと褒めてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170005, /*unit_id*/117001, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/15.4, /*change_face*/2, /*description*/"クスクス…真夏の海で二人きり…\n私だけを…見てくださいね…\nよそ見をしたらどうなるかは\n…秘密です…クスクス…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170006, /*unit_id*/117031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の夜空に花を咲かせましょう\nあなた様が私以外全て\n忘れてしまうくらい…めくるめく\n夢のような花火を…クスクスクス", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170007, /*unit_id*/117031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私とあなた様が結ばれるべき\n運命だということを…この夏に\n証明して差し上げますわ\nどんな手段を使ってでも…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
