INSERT INTO `unit_comments` VALUES (/*id*/1170009, /*unit_id*/117031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"昼も夜もあなた様のことを\n考えてしまいます…あなた様も\nずっと私のことを考えて\nくださいね…絶対に…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170010, /*unit_id*/117031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"寄せては返す波のように\n空に瞬く花火のように\nずっとあなた様といたいのです…\n愛していますわ、あなた様…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170011, /*unit_id*/117001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.4, /*change_face*/5, /*description*/"お誕生日おめでとうございます\nあなた様…特製の薬草茶で\n乾杯しましょう…そして薬草\n料理のフルコース…クスクス…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1170012, /*unit_id*/117031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなた様の誕生日…この日を\n共に迎えることができて私は\n幸せですわ…これからも毎年\n私とだけお祝いいたしましょう…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171001, /*unit_id*/117101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.0, /*change_face*/1, /*description*/"二人で一緒に海デート♪\n仲良しきょうだいだもん\nこれくらい普通だよねっ！\nあ、腕組んで歩こっ、弟くんっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171002, /*unit_id*/117101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.2, /*change_face*/2, /*description*/"熱い日差しにキラキラ輝く海！\nそして水着のお姉ちゃん♪\n弟くん、ワクワクしてこないっ？\nねぇねぇ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171003, /*unit_id*/117101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.3, /*change_face*/5, /*description*/"この水着、過激すぎないよね？\n弟くんを誘惑しすぎないよう\n気をつけなくちゃ…だけど…\nもう少しだけ、大胆にしちゃお♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171004, /*unit_id*/117101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.6, /*change_face*/2, /*description*/"弟くんが海で危ない目にあっても\nお姉ちゃんがいるから大丈夫！\n絶対にキミのそばを離れたり\nしないんだからねっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171005, /*unit_id*/117101, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/7.5, /*change_face*/2, /*description*/"ねぇ、もっとくっついて歩こう？\n周りの人たちに見せつけたいんだ\nお姉ちゃんの自慢の弟を♪\nえへへ、照れない照れない♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171006, /*unit_id*/117131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くん、休憩中かな\nお姉ちゃんも一緒に休んでいい？\nふふっ、あんまり見つめられたら\nお姉ちゃん、照れちゃうな…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171007, /*unit_id*/117131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねっ、次はなにしてあそぼっか！\n弟くんが楽しめること\nぜ～んぶやっちゃおう♪\nほら、立って立って♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171008, /*unit_id*/117131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この水着ね\n弟くんが好きそうなのを\n一生懸命選んだんだよ？\nじ～っくり、堪能してねっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171009, /*unit_id*/117131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くん、暑いでしょ？\nお姉ちゃんが冷やしてあげよっか\n…ふ～っ♪\nえへへ、涼しいでしょ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1171010, /*unit_id*/117131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんと一緒に海水浴なんて\nお姉ちゃん、ドキドキしちゃうな\n…ねぇ、弟くん？　私たち…\n周りからはどう見えるのかな…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
