INSERT INTO `unit_comments` VALUES (/*id*/1172001, /*unit_id*/117201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.6, /*change_face*/7, /*description*/"夏といえばアイドルの季節！\n歌って、踊って、キラキラして！\nみーんなまとめて\n笑顔にしちゃうから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172002, /*unit_id*/117201, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.3, /*change_face*/1, /*description*/"アイドルって結構、\n体力勝負なとこあるんだよね\n今度、砂浜で走りこもうっと！\nキミも一緒に走る？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172003, /*unit_id*/117201, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.7, /*change_face*/1, /*description*/"ねえねえ！　せっかくの夏だし、\nお化け屋敷にでも行こう！\n怖くなっちゃったら、\n私の手を握ってもいいよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172004, /*unit_id*/117201, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/7.3, /*change_face*/2, /*description*/"水着のために、\nダイエット頑張ったんだよ！\n誰か褒めてくれないかなー？\n…えへへ、ありがと！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172005, /*unit_id*/117201, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.4, /*change_face*/2, /*description*/"夏の紫外線はすっごく強烈なの！\nキミも気をつけた方がいいよ？\n…というわけで\n私の日傘で相合い傘しよっか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172006, /*unit_id*/117231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水着はアイドルを輝かせる\n特別な衣装なの！\nいつもより更にキラッキラな私に\n夢中になっちゃってもいいよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172007, /*unit_id*/117231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海も空もきれいだけど、\nノゾミの方がもっときれいだよ…\n的なこと言ってもいいんだよ？\nなーんてね、ふふっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172008, /*unit_id*/117231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今度、いつキミと海に来られるか\n分かんないし…\n今日は徹底的に遊んで\nいーっぱい思い出、作っちゃお♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172009, /*unit_id*/117231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私って人気アイドルなんだよ？\nそんな私の水着を見たのに、\n普段通りの反応？　ふふふ、\nキミのそういうとこ、好きだよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172010, /*unit_id*/117231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きゃっ！　…もー、やったな～！\n私も水かけちゃうから！　えい！\n…なんかバカップルっぽいね！\nあはは！　さ、もっと楽しもう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172011, /*unit_id*/117201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.0, /*change_face*/5, /*description*/"誕生日おめでとー！\nキミの好きそうなアイスケーキ、\n人気のお店で買ってきたよ♪\n後で、一緒に食べようね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1172012, /*unit_id*/117231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\nキミへのプレゼントは…\n私を今日だけ独り占めできる券！\n…なんて、どうかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173001, /*unit_id*/117301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.8, /*change_face*/2, /*description*/"きらめく太陽に美しい海…\n夏まっさかり、ですね\nあなたと過ごす夏がどんなものに\nなるのか…楽しみです。ふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173002, /*unit_id*/117301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"この衣装、ツムギが\n作ってくれたんです\n少し恥ずかしいですけど…\nでも、大好きな衣装です", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173003, /*unit_id*/117301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.6, /*change_face*/6, /*description*/"ビーチバレーにバーベキュー…\n夏にやってみたいこと、\nたくさんあるんです！\n…少し浮かれ過ぎでしょうか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173004, /*unit_id*/117301, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/8.2, /*change_face*/4, /*description*/"日焼けには気をつけないと\n…え？　日焼け止めを塗るの\n手伝おうかって…え、えっと\nでは…お、お願い…します…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173005, /*unit_id*/117301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.5, /*change_face*/5, /*description*/"波の音が好きなんです\nまるで心が洗われるようで…\n…よければ、ですけど\n一緒に、海を見に行きませんか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173006, /*unit_id*/117331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏は開放的な気持ちになりますね\n今なら素直に\n自分の想いを歌に込められそう…\n私の夏の歌、聴いてもらえますか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173007, /*unit_id*/117331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海で歌うのがこんなにも\n楽しいなんて知りませんでした\nあなたも一緒に歌いませんか？\n夏の思い出を共に作りたいんです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173008, /*unit_id*/117331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"唱喚士として、アイドルとして…\nみんなの期待に応えるためにも\nこの夏、もっと成長したいです\nどうか見守っていてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173009, /*unit_id*/117331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"まるで人魚みたい、ですか…？\nなら、おとぎ話のように、\n歌であなたを魅了しちゃいます\n…なんて、ふふ。冗談です", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173010, /*unit_id*/117331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今までの人生の中で\nこの夏が一番ドキドキして\n一番、幸せでした\n…あなたのおかげです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173011, /*unit_id*/117301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.2, /*change_face*/1, /*description*/"ハッピーバースデー！\nあなたと過ごした夏は、\n私にとって大切な思い出です\n今後ともよろしくお願いしますね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1173012, /*unit_id*/117331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日、おめでとうございます\n夏の太陽のようなあなたに贈る\nバースデーソングを作りました\n気に入ってくれるといいのですが", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174001, /*unit_id*/117401, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.6, /*change_face*/1, /*description*/"いちにーさんしー…\nえ？　何してるのかって\nライブの練習ですよ\n騎士さんも一緒にやります？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174002, /*unit_id*/117401, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/7.7, /*change_face*/1, /*description*/"レイ様と海へ来たかったな…\nあ、別に騎士さんと一緒が\n不満ってわけじゃないですよ！？\nむしろ結構楽しんでますから！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174003, /*unit_id*/117401, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.8, /*change_face*/1, /*description*/"騎士さんもう疲れたんですか？\nもう、だらしがないですねぇ\n夏バテなら大変なんで\nこっちで一緒に涼みましょうか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174004, /*unit_id*/117401, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/3.6, /*change_face*/6, /*description*/"今日は仕事のことを忘れて\n遊びますよ！\n…あれ、あの子の水着かわいい\n次のデザインの参考にしよう…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174005, /*unit_id*/117401, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.3, /*change_face*/3, /*description*/"さっきから何見てるんですか？\n言いたいことはハッキリと…\nあ、でも変なこと言ったら\n縛りますからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174006, /*unit_id*/117431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ん～！　いい天気ですね！\n風も気持ちい～！\n今日は思いっきり遊びますよ！\n騎士さんも付き合ってください！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174007, /*unit_id*/117431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですか、この水着！\nサマーライブのために\n私が作った衣装なんですよ！\nかわいいでしょう！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174008, /*unit_id*/117431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですか？\nアイドルと一緒に過ごす夏は？\n…すごく楽しいって\nほんと騎士さんは素直ですね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174009, /*unit_id*/117431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士さん、泳げないんですか？\nそれなら浅瀬へ行きましょう\n…一人で泳いでたら\n騎士さんと来た意味ないですし…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174010, /*unit_id*/117431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あれ？　この浜辺よく見たら\nカップルだらけじゃないですか…\nということは私たちも…\nだとしても私は気にしませんよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174011, /*unit_id*/117401, /*use_type*/2, /*voice_id*/6, /*face_id*/3, /*change_time*/5.1, /*change_face*/5, /*description*/"レイ様があげるというので\n私からもプレゼントです\nべ、別に仕方なくじゃないです\nちゃんと心は込めてるので！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1174012, /*unit_id*/117431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大したものじゃないですけど\n作ってみたのでどーぞ\nどうして僕にって…\nわ、私も誕生日ぐらい祝います！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1175001, /*unit_id*/117501, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"さあ、診察を始めましょう\n最初の患者は…もちろん君よ\n私の助手を務めてもらうからには\n健康でいてもらわないと♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1175002, /*unit_id*/117501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.4, /*change_face*/5, /*description*/"特別にあつらえた\nこの服、どう？\nふふっ、君には少しだけ\n刺激が強いかしら♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
