INSERT INTO `unit_comments` VALUES (/*id*/1178008, /*unit_id*/117831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜は短し騒げよ乙女デース！\n朝まで踊り明かして\nハロウィンを満喫しまショウ！\nソイヤソイヤ、ソレソレデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1178009, /*unit_id*/117831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンもハロウィンを\nお楽しみデスか？\n今夜はブレイコウで\nはしゃぎまくるデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1178010, /*unit_id*/117831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンと過ごすお祭りは\n格別デス！　…ショーグンは\nどうデスか？　ワタシと過ごせて\nうれしいデスか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1178011, /*unit_id*/117801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ショーグンのお誕生日を\nお祝いするデス！　バースデー\nソングをこの笛で演奏しマス！\nピッピヒョーロロープープーー♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1178012, /*unit_id*/117831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日アッパレデース！\nショーグンのためにパレードを\n用意しまシタよー！\n盛大にお祝いするデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1179001, /*unit_id*/117901, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.3, /*change_face*/1, /*description*/"ハロウィンのお出かけすんの\nちょー楽しみにしてたんだ～♪\nうち見たいとこいっぱいあるし\nヒデサイも付き合ってよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1179002, /*unit_id*/117901, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.7, /*change_face*/1, /*description*/"カボチャ、カボチャ～♪\nヒデサイってカボチャ好き？\n今度料理作ったげよっか\nうち、腕によりをかけちゃうし！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1179003, /*unit_id*/117901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.9, /*change_face*/7, /*description*/"ハロウィンセイバーズの\n制服、もにカッコカワで\nうち的にもけっこー\nお気に入りなんだ～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
