INSERT INTO `unit_comments` VALUES (/*id*/1179006, /*unit_id*/117931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんなに楽しいハロウィン\nうち初めてかも！\nヒデサイも楽しんでる～？\nもっとテンション上げてこ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1179007, /*unit_id*/117931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キャンディーにクッキーに…\nみんなに配るお菓子の\nチョイスってこんな感じでおけ？\nいっぱい配ろうね、ヒデサイ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1179008, /*unit_id*/117931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハロウィンのお菓子って\nもにカワすぎてヤバくない？\n食べるのもったいないし…\nうー、ヒデサイどーしよ～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
