INSERT INTO `unit_comments` VALUES (/*id*/1180003, /*unit_id*/118001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"【リッチモンド商工会】は\nわたくしが一から作り上げた\n才能と努力の結晶なのですわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180004, /*unit_id*/118001, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.1, /*change_face*/1, /*description*/"貴方、あの甘ちょろ陛下の\n騎士なのですって？\nずいぶん酔狂なものですわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
