INSERT INTO `unit_comments` VALUES (/*id*/1180006, /*unit_id*/118031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"金銀財宝！\n宝石に美術品！\nこうして囲まれていると…ああ！\n心が潤うようですわ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180007, /*unit_id*/118031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"所詮、この世はお金です！\n金さえあれば、\nなんでもできますわ♪\n…そう思っておりましたけど…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180008, /*unit_id*/118031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この世には、金銀財宝にも劣らず\n輝くものがありますのね\nたとえば…そう！\nわたくしの忠義の心とかっ！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180009, /*unit_id*/118031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちょっとちょっと秘書1号？\n定時だからって\n上がれませんわよ？\nまだ仕事は残っておりましてよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180010, /*unit_id*/118031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"仕事はまだまだ山積み…\nこうなったら少年、貴方にも\n手伝わせて差し上げますわ！\nお礼の方は、応相談でしてよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180011, /*unit_id*/118001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/2, /*description*/"貴方、誕生日ですって？\nそれはおめでとうございます\n何か食べたいものはありまして？\nご馳走くらいしてあげますわよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1180012, /*unit_id*/118031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.5, /*change_face*/1, /*description*/"お誕生日、おめでとうございます\n陛下の騎士として、\n今後とも一層励んでくださいな\nわたくしも手助けいたしますゆえ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181001, /*unit_id*/118101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.6, /*change_face*/1, /*description*/"歌っている時は…落ち着くの…\nほかのうるさい音も、\n自分の声で、紛れるから…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181002, /*unit_id*/118101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/7.8, /*change_face*/5, /*description*/"えっ？\nまた、膝枕…してほしいの？\nま、待って…\n心の、準備がっ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181003, /*unit_id*/118101, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/11.5, /*change_face*/5, /*description*/"ときどき…詩を書いたりもするの\n…あなたの詩も書いたけれど…\nダ、ダメ…見せられないわ\n恥ずかしいもの…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181004, /*unit_id*/118101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.4, /*change_face*/2, /*description*/"敵だったわたしに\n手を差し伸べてくれた\nあなたの気持ちに…\n少しでも、応えたいの…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181005, /*unit_id*/118101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/12.2, /*change_face*/7, /*description*/"話すのは苦手だけど、\nあなたと話すのは…好き…\nあっ…あなたが嫌だったら、\n無理、しないで…いいけど…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181006, /*unit_id*/118131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…あなたに救われてばかりの\n自分では、いたくないの…\n次は、わたしがあなたを\n助ける番…。…だからっ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1181007, /*unit_id*/118131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"人を傷つけるためではなく…\n守るために、歌います。\nもう二度と、大切な人を\n失わないように…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
