INSERT INTO `unit_comments` VALUES (/*id*/1190012, /*unit_id*/119031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー♪\n誕生日プレゼントとして\n学校じゃ習わないようなことも\n今日は特別に教えてあげる♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1191001, /*unit_id*/119101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"闇のドM魔法少女クウカです～♪\n困っている人はいませんか～？\nクウカがお手伝いしますよ～♪\nドMになれば何でも解決です～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1191002, /*unit_id*/119101, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/11.1, /*change_face*/5, /*description*/"愛と希望の妄想が捗ります～！\n「おいクウカ、お前の決めポーズ\nはこれだ」そう言ってドSさんは\nクウカを縛り上げ…ぐふふ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1191003, /*unit_id*/119101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.9, /*change_face*/2, /*description*/"変身するときの台詞、ですか？\nいえ、クウカにはありませんね…\n興奮が頂点に達すると、勝手に\n変態、いえ変身してしまうので…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1191004, /*unit_id*/119101, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/7.7, /*change_face*/5, /*description*/"ドSさんとクウカのアジト…\nこ、ここでクウカに何をしようと\nいうのですかっ！？　ぐふふ…\n何があってもご褒美です～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1191005, /*unit_id*/119101, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/7.8, /*change_face*/4, /*description*/"はっ！？　正義の味方になって\nしまったらドSさんに攻められる\nチャンスが減るのでは…？\nクウカ、それだけは困ります～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1191006, /*unit_id*/119131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"クウカ、ドMビーーーーーーム！\nぐふふ、今日も街の平和を\n守ってしまいました～♪\nみなさん、幸せそうです～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
