INSERT INTO `unit_comments` VALUES (/*id*/1191008, /*unit_id*/119131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"「魔法少女め、俺がその体に\n敗北を刻んでやるよ…」\nああっ、いけませんドSさん！\nクウカ闇に染まっちゃいます～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
