INSERT INTO `unit_comments` VALUES (/*id*/1192001, /*unit_id*/119201, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/5.8, /*change_face*/1, /*description*/"施設の修繕に雪かきに…\n冬だからって休んでられねぇど\n今日も気張って働くべや！\nあんちゃんもよろしく頼むべ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1192002, /*unit_id*/119201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/"夏には夏の、冬には冬の\nおいしい食べ物があるべよ！\nうちで採れた旬の味を\nあんちゃんにも食べてほしいべ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
