INSERT INTO `unit_comments` VALUES (/*id*/1199002, /*unit_id*/119901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.2, /*change_face*/6, /*description*/"サンタは空を飛んで\nどこへでも行けるって聞いたの\n…もしかしてサンタは幽霊なの？\nミヤコと同じなの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199003, /*unit_id*/119901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.4, /*change_face*/2, /*description*/"ミヤコのほしいクリスマス\nプレゼントは～、まずプリン！\n次にプリン、それとプリンなの！\nちゃんと覚えとくの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199004, /*unit_id*/119901, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/3.6, /*change_face*/1, /*description*/"幽霊でも寒いものは寒いの\nオマエの服の中に入れろなの♪\nむふふ、抵抗してもむだなの！\nすり抜けて入ってやるの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199005, /*unit_id*/119901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"クリスマスはミヤコとずっと\nくっついててもらうの～！\n嫌がっても憑いてくの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199006, /*unit_id*/119931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミヤコサンタのお通りなの～！\nプリンをくれたら、お返しに\nプレゼントをあげるの！\nうぃんうぃんってやつなの～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199007, /*unit_id*/119931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"サンタはトナカイとソリがないと\n飛べないらしいの～、でもでも！\nミヤコは一人で飛べちゃうの！\nミヤコサンタはすごいの～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199008, /*unit_id*/119931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"プリンは全部ミヤコのだけど…\nたまにはミヤコが誰かに\nプレゼントするのも悪くないの♪\nなんだかあったかい気分になるの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199009, /*unit_id*/119931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミヤコは優しいから、オマエに\n内緒でプレゼントを用意したの♪\n…あっ、言っちゃったの～！\nもう内緒じゃなくなったの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199010, /*unit_id*/119931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オマエと一緒にいると、毎日が\nクリスマスみたいな気分なの♪\nとーってもワクワクするの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199011, /*unit_id*/119901, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/8.3, /*change_face*/3, /*description*/"今日はオマエの誕生日なの！\n特別にプリンを一口だけ\n分けてあげるの～！\n…それ以上食べたら許さないの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1199012, /*unit_id*/119931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデーなの～！\nお祝いのプリンケーキなの！\nほら、早くローソク吹き消すの！\nミヤコも食べたいの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200001, /*unit_id*/120001, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/7.5, /*change_face*/1, /*description*/"むむっ、闇の気配がするね！\n弟くんの平和を守ることが\n魔法少女なお姉ちゃんの使命！\nあ、街の平和ももちろん守るよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200002, /*unit_id*/120001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/8.1, /*change_face*/5, /*description*/"ふふっ、黒っぽい衣装って\nなんだか新鮮だよ～\nお姉ちゃんって清純派だからね♪\n…えへへ、ワルイコトしちゃう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200003, /*unit_id*/120001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/1.5, /*change_face*/1, /*description*/"へんし～んっ！\n…あっ、弟くん、見てたの？\n変身シーンに興味津々かな？\nじゃあもう一回…見せてあげるね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200004, /*unit_id*/120001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"つよ～い愛と希望が\n魔法少女の力の源なんだって\nだったらお姉ちゃんはいつだって\n最強無敵の魔法少女だね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200005, /*unit_id*/120001, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.3, /*change_face*/5, /*description*/"あっ、なんだか闇の力が\nあふれて暴走しちゃいそう…！\n今日は弟くんをひとりじめして\nたくさん愛でないと…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200006, /*unit_id*/120031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お姉ちゃんの愛の結晶！\n今、ここに合体！\nおいでっ、キング弟くんっ！\nふふっ、どかーんとやっちゃえ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200007, /*unit_id*/120031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"じゃ～んっ♪\n魔法少女のお姉ちゃんだよっ！\nね、どうかな？　うれしい？\n変身、もう一回見せてあげよっか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200008, /*unit_id*/120031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんっ♪\n決めポーズのリクエストはある？\nお姉ちゃんがどんなポーズでも\nバッチリ決めてあげちゃうぞ…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200009, /*unit_id*/120031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねっ、すっごく大きいでしょ♪\nこれはね、愛の大きさなんだよ？\nお姉ちゃんがどれだけ弟くんを\n想ってるか…伝わったかな♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200010, /*unit_id*/120031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんを守れるのなら\n愛でも、希望でも\n…闇の力でも構わないよ\n全部、お姉ちゃん力なんだから♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200011, /*unit_id*/120001, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/3.5, /*change_face*/2, /*description*/"今日はお姉ちゃんにとって\n何よりも大事な日…♪\nそう、弟くんの誕生日！\n魔法の力で、お祝いしちゃうぞ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1200012, /*unit_id*/120031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうっ♪\n魔法少女のお姉ちゃんから\n弟くんへのプレゼント♪\n愛と希望の…魔法だよっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207001, /*unit_id*/120701, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.4, /*change_face*/4, /*description*/"ど、どうかしら…？\nコッコロさんと一緒に選んだの…\n着付けも慣れてないし…\nその…変なところはない…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207002, /*unit_id*/120701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.4, /*change_face*/7, /*description*/"せっかくの正月だものね\n私もたまには息抜きして\nあなたと新年の思い出を\n作ろうと思うわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207003, /*unit_id*/120701, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/10.7, /*change_face*/2, /*description*/"『記念に写真を撮ろう』って…\nイ、イヤじゃないけど\n私一人だけじゃなくて…\nあなたとも一緒に撮りたいわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207004, /*unit_id*/120701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.2, /*change_face*/7, /*description*/"…ん。絶好のお参り日和ね\nさぁ、早く行きましょう\nあなたと過ごす貴重な\n休日を無駄にしたくないもの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207005, /*unit_id*/120701, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/8.2, /*change_face*/4, /*description*/"【美食殿】の一員として\n正月の名物料理は\n全て堪能してみせるわ\n魔物の肉でも…が、がんばる…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207006, /*unit_id*/120731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"待たせてごめんなさい\n着付けに時間がかかっちゃって…\n…え？　似合ってる？\nあ、ありがとう…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207007, /*unit_id*/120731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おせち料理…食べすぎちゃった…\n…でも。食べないで後悔するより\n食べて後悔する日があっても\nいいわよね…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207008, /*unit_id*/120731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『今の私』にとって\n正月を過ごすのは初めてだから\nあなたの行きたい場所があれば\n私も一緒に連れて行ってくれる？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207009, /*unit_id*/120731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"正月ってすごいわね…\nまるでお祭りみたい…\nええと…迷子にならないように\n裾…掴んでてもいいかしら…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207010, /*unit_id*/120731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたはお参りで何を願ったの？\n私は…『今の私』にとって\n大切にしたいことを…\n絶対…叶えてみせるわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207011, /*unit_id*/120701, /*use_type*/2, /*voice_id*/6, /*face_id*/7, /*change_time*/10.8, /*change_face*/2, /*description*/"誕生日、おめでとう\n正月もそうだったけれど\n一緒にお祝いできてうれしい…\nこれからもよろしくね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1207012, /*unit_id*/120731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日ケーキを作ってみたの…\n作るのはたぶん、初めてで…\nあまりキレイじゃないけど…\nでも、心は込めて…作ったから…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208001, /*unit_id*/120801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"正月ってのはいくつになっても\n心が躍るもんだね\nこの時ばかりは一緒に浮かれ\n楽しむのが粋ってもんさ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208002, /*unit_id*/120801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/6.0, /*change_face*/5, /*description*/"どうだいお前さん、アタシの\n晴れ着姿は。キマってるだろ？\nえ、キレイだって？\nあ、ありがとな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208003, /*unit_id*/120801, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/7.4, /*change_face*/1, /*description*/"羽根つき、たこあげ、福笑い\n正月の遊びってのは素朴で\n楽しいもんさ。どうだい？\nアタシと一勝負しないか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208004, /*unit_id*/120801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.1, /*change_face*/2, /*description*/"正月休みを利用して遠出する\nって人も結構いるみたいだね\nお前さんとならそういう\n過ごし方も悪くないかもね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208005, /*unit_id*/120801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.8, /*change_face*/1, /*description*/"おせちってのは作るのに\n手間がかかるんだよね\nまあ、食べたいって言うなら\n作るのもやぶさかじゃないさ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208006, /*unit_id*/120831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシの剣は見せ物じゃないけど\n今日だけは特別さ\nとっておきの舞いを\nとくとご覧あれ、ってな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208007, /*unit_id*/120831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"冷たい空気に触れると身も心も\n研ぎ澄まされていくね\n今なら新しいアタシを\nお披露目できそうだよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208008, /*unit_id*/120831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おや、お前さんもこの剣舞に\n興味があるのかい？\nなら手取り足取りキッチリと\n仕込んでやるよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208009, /*unit_id*/120831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前さんに降りかかる災いは\nアタシが全部斬り伏せる\n大船に乗ったつもりで\nいておくれよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208010, /*unit_id*/120831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうしたんだいお前さん\nぼうっとしてさ？\nえ、アタシに見惚れてた？\nて、照れるじゃないかまったく…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208011, /*unit_id*/120801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/3.5, /*change_face*/1, /*description*/"誕生日おめでとう、お前さん\n今日はお前さんの成長を祝って\nとっておきの釣りスポットに\n連れてってやるよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1208012, /*unit_id*/120831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前さん、今日誕生日だって？\nふーん…いや、この一年で\n随分と見違えたなと思ってね\nなんだかうれしくなっちまうよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209001, /*unit_id*/120901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"見事な晴れ着姿じゃろう？\n特にこの羽織りものは、自分でも\n気に入っておる。いかにも支配者\nらしい風格があるからのう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209002, /*unit_id*/120901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/12.0, /*change_face*/2, /*description*/"わらわは年長者じゃからのう\nすべての者にお年玉をやる権利が\nある…ということにほかなるまい\n景気よくばらまいてやるのじゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209003, /*unit_id*/120901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/"眷属のコウモリたちにも\n正月休みをやるとするか…\nおっと、お主には年中無休で\nわらわのそばにいてもらうぞ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209004, /*unit_id*/120901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/10.5, /*change_face*/2, /*description*/"この姿を民草に披露するか\nお主にじっくり堪能させてやるか\n…今は後者の気分じゃな。お主、\n年明けから運に恵まれたのう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209005, /*unit_id*/120901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.4, /*change_face*/4, /*description*/"子供の姿の晴れ着もかわい\nかった？　いくらおだててもあの\n姿には戻らぬぞ。断じて戻らぬ！\nまあ褒められて悪い気はせぬが…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209006, /*unit_id*/120931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"正月を制覇するには\nまず形から…この華々しい\n艶姿でもって、民草の信望を\n欲しいままにするのじゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209007, /*unit_id*/120931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"後光が差して見えるじゃと？\nふふ、忌々しい太陽もわらわの\n美貌に見惚れるあまり、\n引き立て役に回ったようじゃの♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209008, /*unit_id*/120931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"晴れ着に合わせて髪も\n編んでみたのじゃが…ふふ\nなかなか見違えたじゃろう？\n何度でも惚れ直すがよいぞ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209009, /*unit_id*/120931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"拝むがよい、称えるがよい！\n今のわらわはいくら褒められても\n足らぬ気分じゃ。お主からの\n賛辞であれば、なおさらのう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209010, /*unit_id*/120931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ほれ、もっと近う寄るのじゃ\nわらわの晴れ着姿、とくと\nその目に焼きつけるがよい\n…お主にだけ特別、じゃぞ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209011, /*unit_id*/120901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ハッピーバースデーなのじゃ！\nわらわほどの支配者ともなれば\n眷属の誕生日は当然把握しておる\nさあ、盛大に祝われるがよい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209012, /*unit_id*/120931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はお主の誕生日じゃな！\nふふ、すでに準備は済んでおるぞ\n正月並みに豪勢な宴で\n祝ってやろうではないか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210001, /*unit_id*/121001, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/4.1, /*change_face*/1, /*description*/"これが、あなたのあふれる想い…\nとてつもない力ですが\n全力を超えて、受け止めます！\nあなたがつなげた絆のために！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210002, /*unit_id*/121001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.1, /*change_face*/2, /*description*/"わたしのそばから離れないで\nくださいね？　王女命令であり\nただのペコリーヌからの\n一生のお願いです☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210003, /*unit_id*/121001, /*use_type*/1, /*voice_id*/3, /*face_id*/7, /*change_time*/7.0, /*change_face*/1, /*description*/"あなたの想い、あなたの力…\n全部受け取って、前に進みます！\nあなたを感じているわたしは\nもう誰にも止められませんよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210004, /*unit_id*/121001, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.8, /*change_face*/1, /*description*/"みんなに元気よく\nおいっす〜☆ って\n挨拶したいです\nそのために頑張りましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210005, /*unit_id*/121001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"あなたと一緒なら、どんな場所に\n行ってもへっちゃらです！\nこれからもずっとずっと冒険を\n続けましょうね！　絶対ですよ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210006, /*unit_id*/121031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"【美食殿】の温かい食卓が\nわたしたちの帰る場所です！\nみんなで手を合わせて\nいただきますって言いましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210007, /*unit_id*/121031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたがつないだ絆を\n全力全開でつなぎとめて\n今日までの思い出と\n明日からの希望を守ります！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210008, /*unit_id*/121031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今だけはお腹空いたって泣き言は\n言いません！　\nみんなで食卓を囲むために\nわたしは剣を振るいます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210009, /*unit_id*/121031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたの強い想いを力に変えて…\n全力を超越しちゃいます！\nあなたがつないだ絆を\nこの体と剣に宿します！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210010, /*unit_id*/121031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたにどんなことがあっても\nずっとわたしが全力全開で\n守ってみせますよ！　だって…\n恋しちゃってるんですから☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210011, /*unit_id*/121001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.1, /*change_face*/5, /*description*/"ひゅうひゅう♪　お誕生日\nおめでとうございます！\n全力全開でお祝いしちゃいます！\n今日はあなたを独り占めです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1210012, /*unit_id*/121031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大切な人の誕生日を祝えるって\nすごく幸せなことなんですよね\nこれからもずっと、おめでとうを\n言わせてくださいね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1211001, /*unit_id*/121101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/6.2, /*change_face*/1, /*description*/"あんたとのつながりを感じる…\n温かくて優しい想いを…\n…今のあたしは、\n誰にも負ける気がしないわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1211002, /*unit_id*/121101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/6.9, /*change_face*/1, /*description*/"気を抜くと魔力が暴走しそう…\nこの力、とんだじゃじゃ馬ね…\n…けど、使いこなしてみせるわ\nあいつのためにも…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1211003, /*unit_id*/121101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"あたしたちの冒険は、\nまだまだこれから、でしょ？\n眠ってるヒマなんてないわよ！\n次なる美食を探しに行きましょ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1211004, /*unit_id*/121101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"…あんたの背負ってるもの、\nあたしにも背負わせなさいよ\nここまで来たら、\n運命共同体ってヤツでしょうが", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1211005, /*unit_id*/121101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.3, /*change_face*/2, /*description*/"夢の中だろうがどこだろうが、\nあんたが大変な時は、\nあたしが必ず助けに行くから\nこの手は…絶対に離さないわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
