INSERT INTO `unit_comments` VALUES (/*id*/1211011, /*unit_id*/121101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"誕生日、おめでと！\n今日はあんたのやりたいことに\n付き合ってあげるわ！\nさ、まずはなにをしましょっか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1211012, /*unit_id*/121131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"その…バースデーケーキ、\n作ってみたの。あんたみたいに、\n上手にできなかったけど…\nと、とにかく食べなさい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212001, /*unit_id*/121201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.6, /*change_face*/6, /*description*/"さあ少年、そこに座って！\n今回の事象についてもっと\n詳しく解析させてもらうね？\nあっ！　もう、逃げないでよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212002, /*unit_id*/121201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さて少年、ここで問題です！\nこの炎みたいなやつを使って\nクレープは作れるでしょうか？\n正解は…今から実験だー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212003, /*unit_id*/121201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/2.5, /*change_face*/1, /*description*/"力の制御？　ああ、大丈夫だよ\nもう慣れたし。少年、もしかして\nアタシが天才なの忘れてた？\n…ありがとう。心配してくれて", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212004, /*unit_id*/121201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ…いやあ、まさか\n少年からこんな贈り物を\n貰えるなんて思ってなくてさ\nついうれしくなっちゃった", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212005, /*unit_id*/121201, /*use_type*/1, /*voice_id*/5, /*face_id*/7, /*change_time*/5.5, /*change_face*/1, /*description*/"やっぱり少年は、アタシすら\n変えてしまえるんだね…\nキミという存在の可能性\nこれからも見守らせてもらうよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212006, /*unit_id*/121231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少年を取り戻すためなら\n未知の事象も解き明かすさ\nキミを選んだ者として\n責任を取らないとね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212007, /*unit_id*/121231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少年。キミと繋がってる\nアタシの心も魂も、\n熱く燃えてるのが分かる？\nこの見た目だけじゃないよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212008, /*unit_id*/121231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"実はさ、キミを待ってる人が\nこの世界には大勢いるんだよね\nその人たちのためにもアタシは\nキミを救うよ、少年！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212009, /*unit_id*/121231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"だいたいさ、アタシから少年を\n取り上げようなんてふざけた話だ\nそんな真似、たとえ世界にだって\nさせてたまるもんか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212010, /*unit_id*/121231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんでアタシ、こうなったんだろ\n少年と強く繋がり過ぎたかな？\nありえるね…愛ゆえの行動が\n二人を結びつけちゃったか～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212011, /*unit_id*/121201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.7, /*change_face*/6, /*description*/"ハッピーバースデー！\nキミへの心を込めた贈り物\n受け取ってくれるかな？\n…なんでクレープって分かった？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1212012, /*unit_id*/121231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう、少年！\nキミの成長はアタシにとって\n何よりも喜ばしいよ。今日は\nアタシも一緒に祝っていいかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213001, /*unit_id*/121301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"お芝居のことなら\nちょっとだけ自信があります\nいつもとは一味違う私を\n見ていてくださいね…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213002, /*unit_id*/121301, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/8.1, /*change_face*/1, /*description*/"舞台裏でも客席でも\nお兄ちゃんが見てくれてると\nちゃんとわかるんです\nなんだかぽかぽかするので…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213003, /*unit_id*/121301, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/"ミサキちゃんに勇気を\nわけてもらったから\n私は舞台に立つ楽しさを\n教えてあげたいな…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213004, /*unit_id*/121301, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/7.9, /*change_face*/1, /*description*/"いまのお稽古、どうでしたか？\nひゃわ…！？\n頭は撫でないでくださいぃ…！\nよかったのは分かったので…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213005, /*unit_id*/121301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"お芝居を見てくれたみんなに\n褒めてもらえるのもうれしいです\nでも…お兄ちゃんによろこんで\nもらえるのも…うれしいな…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213006, /*unit_id*/121331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今はまだ、つぼみのような\n未熟な私だけど…\nいつかこの花のように美しく\n舞台の上で咲いてみせます…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
