INSERT INTO `unit_comments` VALUES (/*id*/1213008, /*unit_id*/121331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お芝居をしているときの私は\n誰にでもなれますけど…\n一番なりたい役を演じるために\nいっぱい頑張りますね…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213009, /*unit_id*/121331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の舞台、よろこんでもらえた\nみたいでよかったですぅ…♪\n…今日は頭を撫でないんですか？\nいえ、なんでもないですよぅ…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
