INSERT INTO `unit_comments` VALUES (/*id*/1213011, /*unit_id*/121301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.8, /*change_face*/2, /*description*/"観客はお兄ちゃんだけだけど…\nお誕生日祝いのお芝居、\n見てくれますか…？\nえへへ、心を込めて演じますね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1213012, /*unit_id*/121331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お祝いの気持ちを込めて\nささやかだけどこの花を贈ります\nお兄ちゃん、お誕生日\nおめでとうございます…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214001, /*unit_id*/121401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.4, /*change_face*/5, /*description*/"どうかしら？　この衣装♪\n大人のレディなら\nどんな服でもばっちり\n着こなしちゃうんだから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214002, /*unit_id*/121401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.6, /*change_face*/2, /*description*/"衣装に合わせて髪型も\n変えてみたんだけど、どう？\n…似合ってる？　ふふん♪\nま、聞くまでもなかったわね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
