INSERT INTO `unit_comments` VALUES (/*id*/1214004, /*unit_id*/121401, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.3, /*change_face*/3, /*description*/"ねえ、衣装の後ろの方\nちょっと直してくれない？\n…ひゃぁっ！？　バカバカ！\nど、どこ触ってんのよ～！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
