INSERT INTO `unit_comments` VALUES (/*id*/1214006, /*unit_id*/121431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お客さんを笑顔にするためには\nアタシたちも笑ってないとね！\n物産展のお仕事は大変だけど\n楽しく働きましょ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214007, /*unit_id*/121431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"歌にダンスにお芝居だって…\nどんなことも完璧にできてこそ\n大人のレディよね？\n挑戦のしがいがあるわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214008, /*unit_id*/121431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねえ、このファンレター書いたの\nアンタでしょ？　アタシの魅力が\nやっと分かってきたようね！\n大事に読ませてもらうわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214009, /*unit_id*/121431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いらっしゃいませ～♪\n大人のフェロモンを振りまいて\nお客さんを呼び込んでみるわ！\n商売繁盛、間違いなしよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214010, /*unit_id*/121431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"本当はね、舞台もお仕事も\n不安でいっぱいだったけど…\nアンタがいたら大丈夫になるの\nだから、その…いつもありがと！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214011, /*unit_id*/121401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/"誕生日おめでとう！\nいつもお世話になってるから\n今日はアタシが特別に\nエスコートしてあげるわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214012, /*unit_id*/121431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日プレゼントはア・タ・シ♪\nもー！　リアクション薄いわよ！\nこのアタシが祝ってるんだから\nもっと素直に喜びなさい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215001, /*unit_id*/121501, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"この怪盗の衣装…\n私にはもったいないくらい\nかっこよくて、着るたびに\nちょっとドキドキしちゃいます…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215002, /*unit_id*/121501, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.5, /*change_face*/4, /*description*/"せせせ、先輩…！　私の仮面\nどこかで見ませんでしたか？　\n…顔に着けてる？　…あっ\nほ、ほんとですね。あははは…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215003, /*unit_id*/121501, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/0.0, /*change_face*/4, /*description*/"この間、怪盗活動に役立つかなと\n思って変装をしてみたんですけど\nそもそもいることに気づいて\nもらえませんでした…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215004, /*unit_id*/121501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"先輩、私が見えてますか？\n…ふふ、それなら安心です\n先輩に見ててもらえれば\n私、勇気が出せるので…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215005, /*unit_id*/121501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.1, /*change_face*/5, /*description*/"誰にも気づかれないっていうのも\n悪いことばかりじゃないですね\n…先輩とゆっくり過ごせますし…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215006, /*unit_id*/121531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜空を駆ける怪盗\nここに参上でしゅっ！\n…あわわ、大事なところで\n噛んじゃったぁ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215007, /*unit_id*/121531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この仮面を着けてると、ほんの\nちょっと大胆になれるんです…\nで、でもっ…空から潜入は\n大胆すぎたかもです～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215008, /*unit_id*/121531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふえぇっ、高い、高いですっ！\nせせせ、先輩っ！　私に掴まって…\nいえ、掴まらせてくださーい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215009, /*unit_id*/121531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大丈夫、大丈夫…\n秘密道具は全部持ってきたはず…\nま、万一忘れ物があっても、\n先輩がいてくれれば大丈夫です！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215010, /*unit_id*/121531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"怪盗活動は怖いこと\nばかりですけど…\nでもこのドキドキする気持ちは\nなんだか嫌じゃないです…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215011, /*unit_id*/121501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.9, /*change_face*/2, /*description*/"先輩…お誕生日…\nおめでとう…ござい、ます…！\nや、やった…ゆっくり喋ったら\n噛まずに言えましちゃ！　…あっ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215012, /*unit_id*/121531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は先輩のお誕生日…なので\nいろんな人にお誕生会の予告状を\n送っておきました！　えへへ…\n盛り上がるといいんですけど…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216001, /*unit_id*/121601, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ふっふっふ…！\nあたしこそは神出鬼没の大怪盗！\n狙ったものは必ずいただくです！\n金銀財宝からボスの座まで！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216002, /*unit_id*/121601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/7.1, /*change_face*/6, /*description*/"下克上のためなら\nどんな手でも使うですよ！\nなーに、バレなきゃいいんです！\n…バレたらもっと大変ですし", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216003, /*unit_id*/121601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.7, /*change_face*/3, /*description*/"どうですか、このマフラー！\nいかにも大怪盗って感じで\nテンション上がるです…って、\n引っ張るのはダメですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216004, /*unit_id*/121601, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"あたしの前でボーっとするなんて\nいい度胸ですね、お兄さん！\n今日のぶんのオヤツが\nなくなっても知らないですよ～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216005, /*unit_id*/121601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.5, /*change_face*/6, /*description*/"べ、別になにもしてないですよ？\n気配を消して、お兄さんの話を\n盗み聞きしてたとかじゃないです\nホントホント、ただの未遂ですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216006, /*unit_id*/121631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぴぇええええ！！！\n大怪盗のかっこいい逃走劇が\nはじまるはずだったのに！\nなんで…こうなるですかぁ～！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216007, /*unit_id*/121631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こ…こここ、こういうときこそ\n冷静になるですよ、あたし！\n落ちついて状況を見て…\nあーうん、落ち続けてるです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216008, /*unit_id*/121631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぴぃっ！？　そ、そうだ！\nこういうときこそ覚醒です！\nドラゴン族の力、目覚めるです！\n真夜中だけど目覚めるです～！！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216009, /*unit_id*/121631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ポーズを決めてるんじゃなくて！\nバランス！　崩して！　落下中！\nピンチなんですよ、お兄さん！\nボーっとしてる場合ですかっ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216010, /*unit_id*/121631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"絶対、絶対受けとめるですよっ？\n前フリじゃないですからねっ？\nしっかりがっちりキャッチです！\n信じてるですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216011, /*unit_id*/121601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"お兄さんの誕生日？\n覚えてるですよ、今日ですよね？\nほーら、どんなもんです！\nうたがったおわびをするですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216012, /*unit_id*/121631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうです！\nしょうがないからプレゼントを\n用意してあげてもいいですよ\nさあ、どーんと言ってみるです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219001, /*unit_id*/121901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.4, /*change_face*/2, /*description*/"海賊衣装に新たな魔剣、\nと来れば次に必要なのは…\nふふ、あとは分かるな？\n…そう！　新しい必殺技だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219002, /*unit_id*/121901, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/9.6, /*change_face*/6, /*description*/"ふふっ、新しい海賊の掟を\n思いついたぞ。記念すべき\n66個目の掟がな！\n…えっ、多すぎる？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219003, /*unit_id*/121901, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.1, /*change_face*/4, /*description*/"この格好をしていれば\n冥風戦記の続編の構想が次々に\n浮かんでくる…と思ったのだが\n現実はそう甘くなかったな…うん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219004, /*unit_id*/121901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.4, /*change_face*/6, /*description*/"新たに覚醒した深淵の力を\n制御すべく、眼帯も新調したぞ！\n…なに？　貴様も着けたい？\nわ、私のでよければ貸そうか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219005, /*unit_id*/121901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/"その格好で泳げるのか、だと？\nふっ、侮るなよ…港町の皆さんに\n泳ぎ方を教わっているからな\nいずれは泳げるようになる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219006, /*unit_id*/121931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やはり船首に立つと胸が躍るな！\n…マストの上？　あ、あそこは\nちょっと高すぎるから…いや、\nあの場所は貴様に譲ってやろう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219007, /*unit_id*/121931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この船に主砲はまだないが\n私の最上究極殲滅魔法が、\nいわば主砲のようなものだ！\n船長にして主砲…かっこいいな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219008, /*unit_id*/121931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いい風が吹いているな…\nほどよくコートがはためくし\nかといって帽子が飛んでいかない\nうむ、絶妙にいい風だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219009, /*unit_id*/121931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっ、こうしてると前世で海原を\n冒険した記憶がよみがえってくる\n私と魂で結ばれた貴様も、\nいずれ思い出すはずだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219010, /*unit_id*/121931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"航海士、操舵士…いや、やはり\n相棒というのがしっくりくるな\n大海賊の唯一無二の相棒…\nそれが貴様だ、シグルド！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219011, /*unit_id*/121901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.2, /*change_face*/5, /*description*/"シグルド、誕生日おめでとう！\nこの日のために財宝も用意した！\nわ、私の手作りなのだが…\nよければ受け取ってほしい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219012, /*unit_id*/121931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は貴様の生誕祭だ、盟友よ！\nふふ、すでに饗宴の準備は\n整えてある…海賊流の宴で\n盛大に祝ってやろう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220001, /*unit_id*/122001, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"船で旅するのも楽しそうですね\n今度、私たちもしてみますか？\n呪われた魔道具探索ツアーとか\nランドソル霊場巡りとか…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220002, /*unit_id*/122001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/10.5, /*change_face*/6, /*description*/"波が穏やかでいい天気ですね\nあの空に飛んでる海鳥たちも\n気持ちよさそう…え、見えない？\nではあれは…霊鳥でしょうか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220003, /*unit_id*/122001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/10.7, /*change_face*/5, /*description*/"この衣装…海賊なのか\n水兵なのかどちらなんでしょう\n…似合ってるから問題ない？\nそうではなくて…えっと…うぅ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220004, /*unit_id*/122001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.2, /*change_face*/2, /*description*/"幽霊になってでも叶えたい夢…\nそれを持てたら素敵ですね\n私にはまだ分かりませんが…\nあなたと一緒に見つけたいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220005, /*unit_id*/122001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.5, /*change_face*/6, /*description*/"潮風が気持ちいいですね\nでも少し風が強くて…\nスカートが…きゃっ！\nみ、見えましたか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220006, /*unit_id*/122031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"星が降るような夜ですね…\nこのまま時が止まってしまえば\nいいのに…なんて\nそんなことを思ってしまいます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220007, /*unit_id*/122031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"剣では救えない\n彷徨える魂たちも\n私と…あなたの力があれば\n救うことができます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220008, /*unit_id*/122031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたが迷った時は\n私にも力にならせてください\n暗夜の灯火となるように\nあなたの進む道を占いますから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220009, /*unit_id*/122031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたと一緒なら\nどこまでも遠くへ\n行けそうな気がします\nこの海の果てまででも…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220010, /*unit_id*/122031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"月明かりと星の光…\nそれにどこまでも続く\n海だけが広がっていて…\n世界に二人きりみたいですね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220011, /*unit_id*/122001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/10.0, /*change_face*/5, /*description*/"お誕生日おめでとうございます\n知り合いの霊から海賊に伝わる\n祝い唄を教えてもらったんです\n喜んでもらえるといいのですが", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220012, /*unit_id*/122031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたのお誕生日のお祝いに\n私からこの舞を捧げます\nこの一年の幸せを祈願して\n心を込めて舞いますね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221001, /*unit_id*/122101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"やっぱり…自然はいいですね～\n小鳥さんや草花さんたちの\nおしゃべりも盛り上がっていて…\n心が落ち着きます…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221002, /*unit_id*/122101, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.4, /*change_face*/3, /*description*/"自然はいつだって、私のことを\n受け入れてくれるんです…！\nそれはもう今か今かと\n待ち受けている勢いでっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221003, /*unit_id*/122101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.8, /*change_face*/2, /*description*/"都会では山籠もりのことを\nキャンプって言うんですよね？\nわぁ〜、キャンプかぁ〜…\nなんだかハイセンスな響き…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221004, /*unit_id*/122101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.4, /*change_face*/2, /*description*/"この服は、昔から山籠もりの時に\nいつも着てるんです！\n自然と向き合うための\n正装のようなものですね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221005, /*unit_id*/122101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今度からは…ぼっち空間も\nＢＢ団の活動拠点にしましょう！\n私の大事な場所ですが…\nあなたならいつでも大歓迎です！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221006, /*unit_id*/122131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今夜は語り明かしましょうね？\nＢＢ団の今後について…！\n私たちの壮大なぼっち卒業計画を\n大自然や星々に聞かせましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221007, /*unit_id*/122131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"風のウワサで聞いたんですけど…\nBBQというものがあるそうです\n親近感がわく名前ですよね！\n多分ぼっちに優しいもののはず♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221008, /*unit_id*/122131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふーふー…心も体も\nあったまります♪　なんでもない\nスープなのに、特別な味がします\nきっと二人だから…ですよね…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221009, /*unit_id*/122131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"二人で力を合わせて\n設営をしてお料理をして…\nキャンプの共同作業は\n非ぼっちの気分が味わえます…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221010, /*unit_id*/122131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜のキャンプ場は…\n本当に静か…ですね…\nまるで私とあなただけの…\nふたりぼっちの世界みたい…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221011, /*unit_id*/122101, /*use_type*/2, /*voice_id*/6, /*face_id*/7, /*change_time*/2.9, /*change_face*/4, /*description*/"お誕生日おめでとうございます！\n…あはは、やっぱり緊張しますね\n私の挨拶、大丈夫でしたか？\n声とか裏返ってませんでした？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221012, /*unit_id*/122131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たんおめ～♪　…すみません！\nどう挨拶しようか考えすぎて\nフランクになりすぎました…！\n改めて、おめでとうございます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222001, /*unit_id*/122201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"どう？　この服、素敵でしょ？\n見た目だけじゃなく機能性も\n抜群なの。キャンプ用品店で\n一目惚れして買っちゃった♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222002, /*unit_id*/122201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/"またまたキャンプ道具を\n買い足しちゃった…\n元を取れるくらい使いたいから\nキミと何度も来なくっちゃね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222003, /*unit_id*/122201, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.8, /*change_face*/1, /*description*/"んー！　なんて開放感なのー！\nもうこの眺めだけで\n麦しゅわが飲めるわ！\nさあ、キャンプを始めましょ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222004, /*unit_id*/122201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"えっへへへ〜♪\nとっことんキャンプ飯で\n飲んじゃうわよ〜♪\n最高の一杯、さいこーっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222005, /*unit_id*/122201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"キャンプって楽しいけど\n不便なこともあるわよね…\nなにか困ったことがあったら\nお姉さんに頼っていいからね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222006, /*unit_id*/122231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すぅ〜…うん、空気がおいしい…\nもうこれだけで永遠に飲めそう…\n自然そのものが最高のおつまみ…\n朝から乾杯…しちゃおっかしら…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222007, /*unit_id*/122231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わ、私だって麦しゅわ以外の\n飲み物も飲むんだからっ！\nそれに、コーヒーなら\nキミと一緒に楽しめるでしょ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222008, /*unit_id*/122231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"朝日って本当にきれい…\nランドソルじゃこんな景色は\nなかなか見られないわよね\n存分に味わいましょう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222009, /*unit_id*/122231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おはよう。コーヒー飲むでしょ？\nふふ、普段とは大違いね…\nだって、私がキミを起こして\n飲み物も二人分あるんだもん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
