INSERT INTO `unit_comments` VALUES (/*id*/1226001, /*unit_id*/122601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.6, /*change_face*/6, /*description*/"浮き輪、借りてきたよ\nこれなら騎士クンも泳げるよね？\n…あれ？　これ、二人用！？\n…一緒に使う？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226002, /*unit_id*/122601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.4, /*change_face*/7, /*description*/"暑い日は水分を欠かさないでね\n喉が渇いてなくてもこまめに…\nダ、ダメだよ騎士クン！\nわたしのはあげられないの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226003, /*unit_id*/122601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/9.6, /*change_face*/6, /*description*/"泳いだ後って眠くなるよね\n弟たちと海に行った時は、\nいつもあの子たちも…\n…騎士クン？　寝ちゃった？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226004, /*unit_id*/122601, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/10.8, /*change_face*/5, /*description*/"日焼け止めを塗って欲しい？\nわ、わたしでよければ…！\n…じゃ、じゃあ、失礼します！\n騎士クンの背中…おっきいな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226005, /*unit_id*/122601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.3, /*change_face*/5, /*description*/"波の音を聞いてるの？\n心が落ち着くよね…\n…ひょっとして、今のわたしたち\nすごくいい雰囲気…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226006, /*unit_id*/122631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すごい星の数…\nこれだけのきらめきに\n照らされたら\n自分の心にうそなんてつけないよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226007, /*unit_id*/122631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この景色を見たこと…\nわたし、忘れないよ\n静けさも、光も、自分の気持ちも\n騎士クンが隣にいてくれたことも", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226008, /*unit_id*/122631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"な、泣いてなんかないよ…！\n―あ、ううん、ちがうよね\nわたし…泣いちゃった。ふふっ\nでも泣けたから…もう大丈夫だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226009, /*unit_id*/122631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…騎士クンと出会った時にね\nわたしの中にわたしとは違う\nもう１人の自分が生まれたの\nそのわたしも、騎士クンが…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226010, /*unit_id*/122631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すぐに焦って、暴走しちゃって…\nこんな私で、いつもごめんね\n…どうしてそうなるのか、って？\nそ、それは…大好きだから…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226011, /*unit_id*/122601, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/9.1, /*change_face*/1, /*description*/"誕生日プレゼントに\n特製のかき氷が食べたいの？\nわたしの魔法で作れるかな…\nちょっと試してみてもいい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1226012, /*unit_id*/122631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、お誕生日おめでとう！\n実はね、花火を買ってきたの\nふ、ふたりで一緒に遊びたくて…\n火の扱いには気をつけようね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227001, /*unit_id*/122701, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/4.1, /*change_face*/2, /*description*/"私は海に来たからって\nはしゃいだりしませんよ\n大人はこういう時、ゆっくりと\n過ごすものなんです！　えへへ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227002, /*unit_id*/122701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.1, /*change_face*/2, /*description*/"日差しには気をつけてくださいね\nこうやって帽子をかぶるとか…\nえ？　持ってない？\n…私の、少しだけ貸してあげます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227003, /*unit_id*/122701, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.0, /*change_face*/6, /*description*/"夏の海はおいしいものがたくさん\nあって、食べすぎちゃいますね…\nあ、今おなかは見ちゃダメです！\nダ、ダメなのに…へんた～いっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227004, /*unit_id*/122701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.2, /*change_face*/3, /*description*/"夏はへんたいふしんしゃにも\n気をつけないといけません\n…私が見張ってますからね！\n絶対に離れちゃダメですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227005, /*unit_id*/122701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/7.4, /*change_face*/2, /*description*/"私がもし溺れちゃったりしたら…\nその…すぐに助けてくださいね？\nその代わり、あなたが危ない時は\n私が助けてあげますからね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227006, /*unit_id*/122731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きゃっ！　なんで～！？\n魔法、失敗しちゃいました…\nビショビショになっちゃったけど\n水着だから平気ですっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227007, /*unit_id*/122731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の海では\n私の魔法も絶好調なんです！\nえへへ、冷たいお水…\nかけてあげましょうか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227008, /*unit_id*/122731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"涼しくて気持ちいいですね♪\nえ？　いつもより楽しそう？\n…子どもっぽい、ですかね…？\n今日は許します、夏の海なので！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227009, /*unit_id*/122731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"も～、水をかけないでください！\nひゃっ、つめた～いっ！\n言うことを聞かない人はこうです\n弱めのコスモブルーフラッシュ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227010, /*unit_id*/122731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんなに海が楽しいなんて\n思いませんでした…あ、あなたの\nおかげかもしれません…あの…\n…大人になったら、また二人で…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227011, /*unit_id*/122701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.1, /*change_face*/5, /*description*/"お誕生日おめでとうございます\nえ？　どうして水着なのかって？\nべ、別に理由はありませんけど\n嬉しく、ありませんでしたか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1227012, /*unit_id*/122731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海でお誕生日をお祝いするのって\n特別感があっていいですよね\n今回はあなたの番ですけど\n私の時もお願いしますねっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228001, /*unit_id*/122801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.7, /*change_face*/1, /*description*/"えへ、かわいい水着でしょー！\n動きやすくてお気に入りなんだ♪\nこの水着で海辺を探検したいな！\nもちろんにいちゃんも一緒だよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228002, /*unit_id*/122801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/8.6, /*change_face*/2, /*description*/"水鉄砲に水風船に熱湯風呂に～\n今日はずぶぬれ系のイタズラを\nにいちゃんに仕掛けちゃうよ！\n水着だからぬれても平気だよね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228003, /*unit_id*/122801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"この水中メガネを使ったら、\n海の中でもきれいに見えるよ！\nねえにいちゃん！　海のお魚と\nにらめっこ勝負しようよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228004, /*unit_id*/122801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"ハリセンボンのトゲトゲに～\nクラゲのチクチク、タコのスミ～\n海の生き物ってイタズラ好きな子\nいっぱいだね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228005, /*unit_id*/122801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.3, /*change_face*/5, /*description*/"やったーにいちゃんと海だー♪\n今日は最高の思い出つくろうよ！\nねっ！　にいちゃん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228006, /*unit_id*/122831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ホントは派手な花火が好きだけど\n線香花火もきれいだね～\nずっとずっと見てたいから\nまだ消えちゃわないでほしいなぁ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228007, /*unit_id*/122831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"線香花火ってきれいなだけじゃ\nなくって、音もかわいいな～\nなんだか線香花火のこと\nもっともっと好きになってくよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228008, /*unit_id*/122831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は海でにいちゃんと遊べて\nとっても楽しかったな～♪\n来年の夏も、その次の夏も\nミソギと一緒に遊ぼうね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228009, /*unit_id*/122831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜の風が気持ちいいけど、\n線香花火が消えちゃうよ～！\nねえ、にいちゃん\n風よけに、ミソギの横きて～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228010, /*unit_id*/122831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わかってるよ、ちゃんとゴミは\n持って帰るってば！　\nきれいな海のほうが探検する時、\n気持ちいいもんね♪　", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228011, /*unit_id*/122801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"にいちゃんの誕生日を\n今日は海でお祝いしよう！\nプレゼントにミソギがいーっぱい\n遊んであげるね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1228012, /*unit_id*/122831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生ケーキはろうそくの代わりに\n花火を使っちゃおっか♪　…あ、\nケーキ用意するの忘れちゃった！\n仕方ないから花火しようよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1229001, /*unit_id*/122901, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/5.2, /*change_face*/2, /*description*/"ねえねえ、おにいちゃん！\n海でなにしてあそぼっか！\nおにいちゃんとなら、なにしても\nぜったいたのしいと思うな♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1229002, /*unit_id*/122901, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/7.2, /*change_face*/5, /*description*/"わぁ、おさかなさんいっぱい！\nヒトデさんもかわいいな～♪\nおにいちゃん、海まで\nつれてきてくれて、ありがとう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1229003, /*unit_id*/122901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.3, /*change_face*/6, /*description*/"キョウカちゃんってたまに、\nへんたいふしんしゃさんって\nおにいちゃんのこと呼ぶけど…\nそれって、なあに？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
