INSERT INTO `unit_comments` VALUES (/*id*/1231001, /*unit_id*/123101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/6.0, /*change_face*/1, /*description*/"都会を離れてこうやって\n自然に触れるのもええなぁ\n危険がいっぱいのジャングルも\n王子はんとなら大丈夫や♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231002, /*unit_id*/123101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/8.0, /*change_face*/6, /*description*/"この辺りは虫さんが多くて\nうち、ちょっとかなんわぁ…\nちょうちょさんとかは\n平気なんやけどなぁ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231003, /*unit_id*/123101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.7, /*change_face*/1, /*description*/"森でのサバイバルやと\n泊まるとこの準備するのも\n一苦労やね…でもみんなで\n一緒に準備するの楽しいわぁ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231004, /*unit_id*/123101, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/3.4, /*change_face*/1, /*description*/"お疲れさんどす、王子はん\nちょうどお茶の準備が\nできましたさかいに\n少し休憩しはったらどうどす？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231005, /*unit_id*/123101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.9, /*change_face*/2, /*description*/"王子はんと一緒なら\nどんなとこを冒険しても\n守ってくれますやろ？\n頼りにしてますえ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231006, /*unit_id*/123131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ついに見つけましたえ！\nあれが伝説の遺跡…\nうちドキドキしてきましたわ\n王子はん、行ってみまひょ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231007, /*unit_id*/123131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"緑のざわめきに鳥さんの声…\nなんや冒険らしい雰囲気に\nなってきましたわぁ♪\n王子はん、準備はよろしおすか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231008, /*unit_id*/123131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"冒険を重ねて、うちも\nけっこう成長してますんよ？\nうちが王子はんを守りますさかい\n王子はんはうちのこと守ってな♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231009, /*unit_id*/123131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おうちに着くまでが冒険どす\n行きはよいよい帰りは怖い\n言いますやろ。冒険も旅行も\n怪我せぇへんのが大事なんよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231010, /*unit_id*/123131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うちのポケットの中には\n愛と勇気とお茶菓子が\nぎょうさん入っとりますんよ～\n王子はんにもお裾分けや♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231011, /*unit_id*/123101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"お誕生日おめでとさん♪\nお祝いにとっておきの紅茶と\nお菓子を用意してますんよ\n森のお茶会にご招待や～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1231012, /*unit_id*/123131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は王子はんの誕生日を\nお祝いする大冒険ツアーや♪\nうちがガイド役務めますさかい\n楽しんでくれるとうれしいわぁ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232001, /*unit_id*/123201, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.7, /*change_face*/1, /*description*/"『いいかげん荷物が多いのによ\nすまねぇな、俺まで担がせて』\nぷうきちは荷物じゃないよ？\nぷうきちはぷうきちだもん♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232002, /*unit_id*/123201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.9, /*change_face*/6, /*description*/"見て見てお兄ちゃん！\n旅のしおりを作ったの♪\nえ？　絵しか描いてないよって？\nいいの～こういうのは気分なの～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232003, /*unit_id*/123201, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"地図をグルグル回すのはね\n方向音痴の証拠なんだって\nってことは、私もお兄ちゃんも\n方向音痴で…おそろいだね～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232004, /*unit_id*/123201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/8.7, /*change_face*/6, /*description*/"じゃ～ん、旅のお洋服だよ♪\n『おお…完璧装備じゃねぇか！\n何が起きても大丈夫だな』\n…でもお兄ちゃんは側にいてね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232005, /*unit_id*/123201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/2.4, /*change_face*/2, /*description*/"私の冒険に終わりはないの\nお兄ちゃんとぷうきちと\nどこまでもどこまでもずーっと\n一緒に進むんだもん♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232006, /*unit_id*/123231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"近道より、遠回りがしたいな～\nだってせっかくのお兄ちゃんと\n一緒の冒険なんだもん♪\nね、あっちにも行ってみようよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232007, /*unit_id*/123231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海のむこうには何があるのかな？\n空のむこうは？　…お兄ちゃんも\n知らないんだ。…じゃあじゃあ、\n想像で言い合いっこしよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
