INSERT INTO `unit_comments` VALUES (/*id*/1232009, /*unit_id*/123231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"冒険って、不安と期待でドキドキ\nするでしょ？　…その感じがね\n私が時々お兄ちゃんに感じる\nあの気持ちと…すごく似てるの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1232010, /*unit_id*/123231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふ、お兄ちゃんも泥だらけだ～\n冒険をがんばった証拠だよね\nお兄ちゃん…カッコいいよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
