INSERT INTO `unit_comments` VALUES (/*id*/1235001, /*unit_id*/123501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.3, /*change_face*/5, /*description*/"ハロウィンだから、街もすごい\n賑わいになってるね～\nあたしは平常運転\nぐうたらハロウィンを満喫するよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235002, /*unit_id*/123501, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/2.1, /*change_face*/1, /*description*/"ん？　この格好？\nキョンシーの仮装だよ～\n…で、キョンシーってなに？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235003, /*unit_id*/123501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"あんたもなにか\nお化けの仮装とかしなよ～\nミイラ男とか、カエル人間とか\nインパクトのあるやつね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235004, /*unit_id*/123501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"ハロウィンのお祭り\nちゃんと楽しんでる？\nあたしは…まぁ、うん\nあんたがいれば、なんでもいいや", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235005, /*unit_id*/123501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/6.5, /*change_face*/2, /*description*/"えっ、あたしとハロウィンに\n参加したいって？\n…分かったよ、行くよ～\nでも、最後までつきあってよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235006, /*unit_id*/123531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"トリックオアドングリ～♪\nあんパンかゲームでもいいけど\nあたしが欲しい物くれないと\nイタズラしちゃうよ～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235007, /*unit_id*/123531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハロウィンってお菓子が\nもらえなかったら、好きなだけ\nぐうたらしていいお祭りだよね？\nえっ？　違うの？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235008, /*unit_id*/123531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これってキョンシーの仮装\nなんだけどさ、袖が\nお気に入りなんだよね～\nだら～っとした感じがさ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235009, /*unit_id*/123531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ところで、あんたはあたしの\n仮装をどう思ってるの？\n…お腹が寒そうだって？\nそういうことじゃないよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235010, /*unit_id*/123531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ゆっくり、のんびり\nハロウィンを見て回ろ～？\n焦ったって、一緒にいられる\n時間が短くなるだけだもん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235011, /*unit_id*/123501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.0, /*change_face*/5, /*description*/"誕生おめでと～\nやだな、忘れるわけないじゃん\n…プレゼントは、さっきもらった\nお菓子でもいいかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1235012, /*unit_id*/123531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日、めでたいね～\nプレゼントはあたし手作りの\nカボチャあんパンだよ\nおかわりもあるからね～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1236001, /*unit_id*/123601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.0, /*change_face*/3, /*description*/"パレードは賑やかで楽しいね\nでも羽目を外しちゃう人は\n私が取り締まっちゃうよ！\nハロウィンの平和は私が守る！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1236002, /*unit_id*/123601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.8, /*change_face*/1, /*description*/"あなたも一緒に仮装しようよ！\n私の隣を歩くならお似合いなのは\n…騎士様？　王子様？\n逆に囚人服とかもありかもね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1236003, /*unit_id*/123601, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.4, /*change_face*/2, /*description*/"ちょっと目を離すと、あなたは\nいつもどこかに行っちゃうね…\n心配だからフラフラできないよう\n手錠かけちゃおうかな♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1236004, /*unit_id*/123601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"ハロウィンだしお化けが出てきて\n私のこと驚かせてくれないかな？\nだってそれなら、怖がるふりして\nあなたに抱きつけちゃうもんね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1236005, /*unit_id*/123601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.3, /*change_face*/2, /*description*/"ハッピーハロウィンって言うけど\nなにがハッピーなんだろうね？\n…私はあなたと一緒に過ごせて\n超超ハッピーなんだけど♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1236006, /*unit_id*/123631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"かわいい飾りや花火に目もくれず\n私を見つめてるのはなんでかな？\nふふ、もしかして、あなたの瞳\n逮捕しちゃった？　なーんてね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
