INSERT INTO `unit_comments` VALUES (/*id*/1238001, /*unit_id*/123801, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.1, /*change_face*/2, /*description*/"おおいワタシを一人にするなよ？\n寂しいとむしずが走るんだよ\n命懸けでかわいがっておくれ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238002, /*unit_id*/123801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"さぁ坊や、互いに狩り合おう！\n坊やがワタシを食らうか\nワタシが坊やを食らうか\nどちらにしても…胸が躍るよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238003, /*unit_id*/123801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"オマエを見ていると\n狩猟本能と庇護欲の\n両方がかき立てられる…\nまったく、稀有な生き物だ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238004, /*unit_id*/123801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"どうだ、この毛並み…\n思う存分、モフりたいだろう？\nしかし残念ながらまだダメだ\n誓約を果たしてから…な☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238005, /*unit_id*/123801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.7, /*change_face*/1, /*description*/"どうだ、愛らしいだろう？\n坊や、ワタシを飼ってみるか？\n飼い殺しにしてもかまわんぞ\nこう見えて主従には堅いのだ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238006, /*unit_id*/123831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ウサギにしては強そうだと？\nあっはは☆　かまわんさ！\nオマエの中のウサギ像を\nワタシ色に塗り替えてやろう…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238007, /*unit_id*/123831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ずいぶんと熱心に見つめるのだな\nいいだろう、教えてやる\nオマエが今感じているのはな…\nギャップ、というヤツだよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238008, /*unit_id*/123831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こうして下から見上げるのも\n悪くない光景だな\nふふ…しばらくそこに立っていろ\nワタシが見飽きるまで、な♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238009, /*unit_id*/123831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ん、尻尾が動いているだと？\nさすがにそれはないさ。鎧だぞ？\n…ま、オマエと二人きりだからな\n尻尾を振りたい気分ではあるが☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238010, /*unit_id*/123831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"耳は繊細な作りのようでね\nどうやらこの鎧の弱点らしい\n…なに、教えていいのかだと？\nふふ、坊やにだけは特別さ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238011, /*unit_id*/123801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.1, /*change_face*/2, /*description*/"今日は坊やの誕生日らしいな\n生き物は皆一人で生まれ\n一人で死んでいくと言うが…\n安心しろ。我々は二人で一匹さ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1238012, /*unit_id*/123831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オマエと出会わなければ\nワタシは退屈の荒野に\n野たれ死んでいたかもしれん\n坊やの誕生に感謝しよう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1239001, /*unit_id*/123901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"これが自分の新しい姿ッス！\nただの鎧じゃないッスよ？\nなんと、お母さんの手作りッス！\nいかしてるッスよね～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1239002, /*unit_id*/123901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"自分のイチ押しは\nなんといってもこのマント！\nかっこいいし、くるまったら\nお昼寝もできるすぐれモノッス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1239003, /*unit_id*/123901, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/3.2, /*change_face*/1, /*description*/"鍛え抜かれた自分の腹筋！\n…には、ほど遠いッスけど…\n魂のほうはもうすごいッスよ！\nモリモリのバッキバキッス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1239004, /*unit_id*/123901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/12.1, /*change_face*/1, /*description*/"耳？　いやー…耳を見せるのは\nまだ恥ずかしいッス…\nずっと見せないようにしてたから\nなんか…すーすーしちゃうんス", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1239005, /*unit_id*/123901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"そうだ！　二人でお揃いの\n鎧を着るのはどうッスか？\nもちろん色違いで！\nうんうん、相棒っぽいッス～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1239006, /*unit_id*/123931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"トラか？　王宮騎士か？\n答えは一つ！　どっちもッス！\nそれが自分─\nマツリの生きざまッス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
