INSERT INTO `unit_comments` VALUES (/*id*/1241001, /*unit_id*/124101, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/7.2, /*change_face*/6, /*description*/"あ、あんまり見ないでよっ\n急に変身したから心の準備が…\nへ？　かわいいから安心して？\nそそそそういう問題じゃな～い！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241002, /*unit_id*/124101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.2, /*change_face*/1, /*description*/"サンタコスのエンジェルとか…\n属性盛り過ぎちゃってない？\nここまできたら、もうどこを\n恥ずかしがればいいのか…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241003, /*unit_id*/124101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.5, /*change_face*/4, /*description*/"ね、ねえ…今の私、大丈夫？\nちゃんとエンジェルに見える？\nただただクリスマスだからって\n浮かれてる人に見えない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
