INSERT INTO `unit_comments` VALUES (/*id*/1241006, /*unit_id*/124131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハ…ハッピークリスマス\nサンタなエンジェルからの…\nううん、私からのプレゼントよ\n…いいから受け取りなさいっ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241007, /*unit_id*/124131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アンタも隠れてっ\nもし、誰かにこんなところを\n見られたら私、私ぃ～…\nぷしゅぅ～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241008, /*unit_id*/124131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この子の名前？　まだないけど…\n好きに呼べばいいんじゃない？\n…コヨリ？　私に似てるから？\nえ…えええええ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241009, /*unit_id*/124131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"深呼吸して…すーはぁ～…\nよしっ。い、今、渡すからっ\nもうちょっとだけ待ってて\n…ううう、がんばれ私！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241010, /*unit_id*/124131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"エンジェルだって…\n「みんな」じゃなくて\n「誰か」を幸せにしたいって\n思うときがあってもいいでしょ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241011, /*unit_id*/124101, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/11.4, /*change_face*/3, /*description*/"ほ、欲しいものとか…ある？\nアンタ、今日アレでしょ？\nいわゆる…誕生日？　でしょ？\nほら、いいから欲しいもの言う！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1241012, /*unit_id*/124131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お、お誕生日よね、今日は\nめ…めでたいわね～。ほんとにっ\nで、で…えっと、でかしたわ！\nと…と？　と～…と、とうッ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242001, /*unit_id*/124201, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/9.8, /*change_face*/2, /*description*/"ほら空には白いハトが飛んでるよ\nまるで羽根が雪のように舞って\n世界が輝いているようだ…\nふふ…少年、メリークリスマス", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242002, /*unit_id*/124201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"いつもより体が軽いのは、鎧を\n脱いでいるからだけじゃない…\n子どもみたいで恥ずかしいけど\n少し聖夜に浮かれているみたいだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242003, /*unit_id*/124201, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/6.5, /*change_face*/6, /*description*/"聖夜の平和を守る慈愛の戦士\n聖女仮面、見参！\n…一応考えてみたけど\n決めポーズはいらないかな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242004, /*unit_id*/124201, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/6.7, /*change_face*/2, /*description*/"うう、突き刺すような寒さだね\n君のほっぺも真っ赤じゃないか\nほら、暖めてあげるから\nこっちにおいで", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242005, /*unit_id*/124201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"見慣れた街もクリスマスの装いで\nキラキラと輝いているね\n…いやそう見えるのは多分\n君が隣にいるからかもな…ふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242006, /*unit_id*/124231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"静かな教会に白いハト\nなんとも神秘的な光景だね\n美しい鳴き声の響きがまるで\nクリスマスキャロルのようだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242007, /*unit_id*/124231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"素顔でいる事も少し慣れてきた\nこのままクリスマスの魔法に\nかかったままでもいいかな…\nああ、いや、冗談だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242008, /*unit_id*/124231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"素顔は皆に晒しても、\n私がジュンだということは秘密だ\nもし私を呼ぶときは別の名前で…\nいや少年。聖女さまはやめてくれ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1242009, /*unit_id*/124231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少年、そうまじまじ見つめるのは\nやめてほしい…恥ずかしい\nほら顔が、トナカイの鼻よりも\n真っ赤になってしまうよ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
