INSERT INTO `unit_comments` VALUES (/*id*/1247011, /*unit_id*/124701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.2, /*change_face*/1, /*description*/"お誕生日おめでとう\n君の存在は私にとって\nまさに百薬の長\n今年も側に常備させてね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1247012, /*unit_id*/124731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"盆と正月がいっぺんにきたって\nこのおめでたさには敵わないわ\nさ、新薬で乾杯したら…\nバースデー治験を始めるわよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248001, /*unit_id*/124801, /*use_type*/1, /*voice_id*/1, /*face_id*/7, /*change_time*/2.8, /*change_face*/1, /*description*/"アイドルの新衣装おひろめ！\n…ってわけじゃないけど\n新しい私の姿を見てもらえるのは\nうれしいな。それで…感想は？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248002, /*unit_id*/124801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.4, /*change_face*/2, /*description*/"みんなを楽園から解放する力…\nそれが形になったのが\nこの姿なんだよね\n開放的なデザインも納得だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248003, /*unit_id*/124801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/11.0, /*change_face*/6, /*description*/"楽園の影響を受けた人たち…\nみんな笑顔だったよね。なのに…\n見てると悲しい気持ちになるの\n…キミも、私と同じみたいだね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248004, /*unit_id*/124801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.0, /*change_face*/7, /*description*/"キミには守ってもらってばっかり\nだから…今度は私がキミを守るよ\nでも、応援はよろしくね♪\nもちろん最前列じゃないとダメ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248005, /*unit_id*/124801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.3, /*change_face*/6, /*description*/"キミもおそろいの格好になる？\nなーんて…え、なってもいい？\n気持ちはうれしいけど…ダメ！\nセクシーすぎるもん、私がＮＧ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248006, /*unit_id*/124831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"「これまで」だって素敵な思い出\n…だから、もっと増やしたい！\nさあ、一緒に行こう！\n「これから」を迎えに！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248007, /*unit_id*/124831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アイドルは強くてかわいい！\nそれが当たり前！　つまり？\n未来のトップアイドルなら最高に\n強くてかわいいのも当たり前っ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248008, /*unit_id*/124831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この輝きは未来の輝き！\n私とキミと、みんなを待ってる\nまだ見たことのない世界の光！\nね、こわくなんかないでしょ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248009, /*unit_id*/124831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミがいつも通りでいてくれる…\nそれだけで私は前を向けるんだ\nたとえどんなに暗い道でも─\n堂々と胸を張って！　笑顔で！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248010, /*unit_id*/124831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"前から好きだった自分の名前…\nもっともっと大好きになったよ！\n私はノゾミ─希望の名前！\nさあ、キミも呼んで！　せーの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248011, /*unit_id*/124801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.4, /*change_face*/1, /*description*/"ハッピーバースデー！\n今日はキミが主役の日だよ！\nいつも応援してもらってる分\n思いっきり目立たせてあげるね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1248012, /*unit_id*/124831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが生まれてきてくれたから\n私たちは出会えたんだよ\nこの奇跡に、私は感謝してるんだ\n心を込めて…誕生日おめでとう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249001, /*unit_id*/124901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"たとえ未来が茨の道でも\n道があるなら歩けるだろ\nなにも迷うことはない\n突き進もうぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249002, /*unit_id*/124901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今がダサくて惨めだろうが\n立ち止まっちまうよりはマシだ\nそういう泥くさい生き方だって\nオレは嫌いじゃないぜ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249003, /*unit_id*/124901, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.3, /*change_face*/1, /*description*/"未来はいつだって\nひとっ走りで行ける距離にある\n自分の気持ちに\n正直でいさえすればな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249004, /*unit_id*/124901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.8, /*change_face*/1, /*description*/"思えばずいぶん遠くまで\n来たもんだよな…\nキミといると、時間の流れが\nやたらと早く感じるぜ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249005, /*unit_id*/124901, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/8.1, /*change_face*/1, /*description*/"オレにも大切なもんがたくさん\nできた。イノリとボスと\nファミリーの連中…\nもちろんキミもその一人だ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249006, /*unit_id*/124931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たぎる！　たぎるぜ！\nどんな過去も置き去りにする力！\nこの拳にみなぎってくる！\nさあ、ケンカだケンカぁ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249007, /*unit_id*/124931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぐずぐず立ち止まってんなよ\nたった一歩でも踏み出せば…\nその先に未来の景色が見える\n踏み込んでこいよ、ここまで！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249008, /*unit_id*/124931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オレ様の未来とテメェの未来\nどっちが強いか、未来バトルと\nいこうじゃねえか！\n負けたほうが飯おごりだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249009, /*unit_id*/124931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミ、遅れるなよ！\n今のオレ様の拳は\nランドソル最速だぜ！\n未来に向かって一直線だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249010, /*unit_id*/124931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな苦難が待っていようが\nオレはもう立ち止まらない\nダチも仲間も、自分自身も！\nまとめて未来へ連れてくぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249011, /*unit_id*/124901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.1, /*change_face*/2, /*description*/"誕生日、おめでとな！\nへへっ、ケーキ食ったら\nアジトまで遊びに来いよ\nファミリー総出で祝ってやるぜ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1249012, /*unit_id*/124931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー、ってな！\n今日は一日、遊び倒そうぜ\n夜が明けるまで\n付き合ってやるからさ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250001, /*unit_id*/125001, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.9, /*change_face*/1, /*description*/"はは、なんだか分かんないけど\nすっごい力を感じるぞ！\nこいつでオマエを守ってやる！\n明日も明後日もその先もな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250002, /*unit_id*/125001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"たまには思い出を\n懐かしむのもいいと思う…\nでもその後はちゃんと前を向いて\n未来へ進まなきゃ駄目だよな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250003, /*unit_id*/125001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"進むべき道に迷った時は\nアタシを見ろ！\nアタシがいればオマエの未来は\nピッカピカに明るいぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250004, /*unit_id*/125001, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/5.5, /*change_face*/1, /*description*/"思い出ってのは未来に進むのを\n邪魔する時もあるんだよな…でも\nオマエとは進む力になるような\n思い出を作っていきたいぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250005, /*unit_id*/125001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/1.6, /*change_face*/2, /*description*/"アタシの望む未来…絶対に\n楽しいに決まってるよな！\nもちろんオマエもそこにいろよ\n約束だからな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250006, /*unit_id*/125031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はーっはっは！　大悪党コンビ\nここにふっかーつ！\n『この感じ、懐かしいね\n柄にもなく心が踊るよ』", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250007, /*unit_id*/125031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『ノウェム、連携が乱れてるよ\nもしかして腕落ちた？』\nああ？　それはこっちの台詞だ！\n久々にオマエの本気見せてみろ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250008, /*unit_id*/125031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシは一人でも最強だけど\nオクトーとなら超最強だ！\nやっぱりアタシたちは\n二人でひとつってことだ、相棒！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250009, /*unit_id*/125031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『ノウェムの隣って妙に落ち\n着くんだよね。言わないけどさ』\nどうしたオクトー？　何か用か？\n『べっつに～』", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250010, /*unit_id*/125031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシたち３人って\n昔は敵だったよな…でも、今は\nこうやって一緒に戦ってる！\n未来って面白いな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250011, /*unit_id*/125001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/1.8, /*change_face*/2, /*description*/"誕生日おめでとう！\n今日は一日アタシが\n一緒にいてやるよ！\nうれしいだろ？　うれしいよな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1250012, /*unit_id*/125031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オマエに誕生日プレゼントを\n用意したぞ！　何かはまだ内緒だ\n悪党ってのは色々秘密がある\nものだからな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251001, /*unit_id*/125101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.0, /*change_face*/2, /*description*/"今日もお疲れさまにゃ！\n休憩に温かいコーヒー飲むにゃ？\nお代は従業員割引で200ルピ！\n福利厚生バッチリ完備にゃ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251002, /*unit_id*/125101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.4, /*change_face*/2, /*description*/"コーヒーとたい焼きのあんこが\n織りなすハーモニー…！\nにゃはは♪　相性バツグンにゃ！\nこのセットは爆売れ必至だにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251003, /*unit_id*/125101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/11.9, /*change_face*/1, /*description*/"猫従業員のシフト組んでるにゃ…\nこの子は連勤だから休ませて…\n…ねえキミ、ヘルプ頼めるにゃ？\n猫みたいにかわいく接客してにゃ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251004, /*unit_id*/125101, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.5, /*change_face*/2, /*description*/"ん？　目が冴えて眠れない？\nにゃはは、コーヒー飲みすぎにゃ\nじゃあ眠くなるまでお姉さんが\n頭ポンポンしてあげるにゃ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251005, /*unit_id*/125101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.6, /*change_face*/5, /*description*/"猫とコーヒーに癒されるにゃ～\nでもあたしにとって最大の癒しは\nいっつもそばで微笑んでくれる\nキミの存在なんだにゃ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251006, /*unit_id*/125131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はい、猫専用マタタビミルク♪\nキミにはキミ専用特製コーヒー♪\nなにが特製かって？　にゃはは！\n愛情をいっぱい入れてるのにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251007, /*unit_id*/125131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"気持ちのいい天気だにゃ～♪\nこんな日はテラスで休憩にゃ！\n太陽の光とコーヒーの香りで\nいっぱい癒されてほしいのにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251008, /*unit_id*/125131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが猫だったらにゃ～\nそしたらずっと一緒にいて\nなでなでしたり、すりすりしたり\nずっと愛でてられるのににゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251009, /*unit_id*/125131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが優しくしてくれるから\nすっかり懐いちゃったにゃ～♪\nにゃ！？　違…猫の話にゃ！\nあたしのことじゃないにゃ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1251010, /*unit_id*/125131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オシャレな外観のカフェで\nキミと働けてすごく楽しいにゃ♪\nこのままずっと二人で一緒に…\nにゃはは…なんてにゃ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
