INSERT INTO `unit_comments` VALUES (/*id*/1251012, /*unit_id*/125131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとにゃ～！\nお祝いにキミの顔のラテアートを\n作ったんだけど、上手くできて\n飲むのがもったいないのにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1252001, /*unit_id*/125201, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.1, /*change_face*/6, /*description*/"…にゃんだおまえ\nおやつがほしいにゃ～？\nって、き、貴公っ！\nいつから聞いていたのだ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1252002, /*unit_id*/125201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.4, /*change_face*/2, /*description*/"コーヒーだけではなく\n一緒にお菓子はどうだ？\nこの間仕入れたチョコレートが\nおすすめだ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
