INSERT INTO `unit_comments` VALUES (/*id*/1252004, /*unit_id*/125201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/8.0, /*change_face*/2, /*description*/"いらっしゃい…って、貴公か\nその顔、もしやなにか相談事か？\nよし、コーヒーでも淹れてやるから\n私に話してみないか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1252005, /*unit_id*/125201, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/1.5, /*change_face*/6, /*description*/"もぐ…なんだ？\nし、新作の味見をしていたのだ！\n貴公も気になるのなら\n一緒に味見してみるか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
