INSERT INTO `unit_comments` VALUES (/*id*/1252010, /*unit_id*/125231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴公のためにコーヒーを淹れたぞ\n…私にも淹れてくれるのかっ？\nふふっ、では一緒にテラスで\n語らおうではないか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1252011, /*unit_id*/125201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.0, /*change_face*/1, /*description*/"今日は貴公の誕生日だからな\n好きなお菓子を選ぶといい！\n…私が淹れたコーヒーがいい？\n相変わらず欲がないな、貴公は", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1252012, /*unit_id*/125231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！\n貴公のために特別なパンケーキを\n用意したから一緒に食べよう♪\n味見をしたが絶品だったぞ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1253001, /*unit_id*/125301, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"この服はいかがでしょうか？\n大自然の中でもしっかりと\nお勤めを果たすため、いろいろ\nこだわった逸品でございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
