INSERT INTO `unit_comments` VALUES (/*id*/1253004, /*unit_id*/125301, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"主さま、わたくしと\n森でお散歩をしませんか？\nたまには街の喧騒を離れ、二人で\n静かに過ごすのもよろしいかと…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1253005, /*unit_id*/125301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.2, /*change_face*/2, /*description*/"森を守るお仕事は大変です\nしかし主さまたちと一緒なら\nやり遂げられると信じております\n今までの冒険と同じように", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1253006, /*unit_id*/125331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森の中で、主さまと\n穏やかなひとときを過ごす…\nわたくしにとって\nこれに勝る幸せはございません", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1253007, /*unit_id*/125331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わんわんのほうの主さまも\n森の中でのんびりできて\n大満足のようでございます\nお腹をなでなでしてあげましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1253008, /*unit_id*/125331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森の中はとても静かで\nわたくしどもしかいないような\n気分になってまいりますね\n和やかな心地がいたします", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1253009, /*unit_id*/125331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ずっとずっと、こうして主さまと\n過ごしていたいです…\nいずれお勤めに戻らなくては\nなりませんが、それでも…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
