INSERT INTO `unit_comments` VALUES (/*id*/1254003, /*unit_id*/125401, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今日はニャットちゃんを洗って…\n…え？　お世話してえらい？\nふふ、私はニャットちゃんの\nママ代わりですからね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254004, /*unit_id*/125401, /*use_type*/1, /*voice_id*/4, /*face_id*/3, /*change_time*/8.1, /*change_face*/2, /*description*/"魔物は怖い生き物。でも…\nレンジャーになってから\n色んなことを考えています\nこれが学ぶということなんですね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254005, /*unit_id*/125401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"見てください\nちょっと筋肉がついてきたんです\nこのままムキムキになれたら…\n私があなたのこと、守れますね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254006, /*unit_id*/125431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"レンジャーの権限において\nターゲットを拘束します\nニャットちゃん、\n私のフォローをお願い！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254007, /*unit_id*/125431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の世界と\nニャットちゃんの世界は違う\nでも同じ場所で生きているから…\nこの『居場所』を一緒に守ろう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254008, /*unit_id*/125431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"人と魔物…\n共生の道は険しいけれど\nできないわけじゃない\n私たちが、その証明になろう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254009, /*unit_id*/125431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ここはニャットちゃんと私で\n対処します\n私たちが成長した姿、\nあなたも見守っててください！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254010, /*unit_id*/125431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弱肉強食は真実だと思うけれど、\nそれが全てなわけじゃない…\n私も誰かを助けて助けられながら\nあなたの隣を歩いていきたいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254011, /*unit_id*/125401, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/2.9, /*change_face*/2, /*description*/"少し、時間ありますか？\nレンジャーの仲間たちと一緒に\nケーキを用意したんです\nその…あなたの誕生日なので…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1254012, /*unit_id*/125431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nそれと、ニャットちゃんからも…\n『いつもありがとにゃん♪』\n…な、なんて", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255001, /*unit_id*/125501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.0, /*change_face*/2, /*description*/"剣に拳、そして魔法…\n気付けばいろんな戦い方が\nできるようになっちゃった\nどんどん私を頼っていいからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255002, /*unit_id*/125501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"魔法使いって結構\nいろんな依頼で重宝されるんだ！\nおかげで騎空艇のための貯金が\nいいペースで貯まってるよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255003, /*unit_id*/125501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.0, /*change_face*/1, /*description*/"陰キャとか陽キャとか\nよく分からないけど…\nどっちのビカラちゃんも大好き！\nあなたもそうだよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255004, /*unit_id*/125501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/7.3, /*change_face*/1, /*description*/"あなたやビカラちゃんたちと\n空を思いっきり冒険できたら\nきっと楽しいだろうなぁ\n…よし、騎空団活動頑張ろっと！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255005, /*unit_id*/125501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.1, /*change_face*/5, /*description*/"ジョブチェンジしたら衣装も\n変わるけど…あなたはどの服が\n好きかな？　…全部好き？\nな、なんだか少し照れるかも…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255006, /*unit_id*/125531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたがそばにいてくれたら\n誰が相手でも負ける気がしない！\n見てて！　この新たな力で\n私たちの道を切り開いてみせる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255007, /*unit_id*/125531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"細かいコントロールは苦手で…\nやりすぎちゃったらゴメンね！\n思いっきり魔力を集中させて…！\nいくよ！　エーテルブラスト！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255008, /*unit_id*/125531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え、かわいいポーズだねって…？\n実は私もお気に入りなんだ！\n魔法詠唱に必要な動作でね\nこうしたら集中できるの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255009, /*unit_id*/125531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたとの冒険はいつだって\nワクワクがいっぱい！\nさあ、次はどこへ行こっか！\nその道は絶対空に続いてるから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255010, /*unit_id*/125531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法は便利で強力な力だけど…\n私の一番の武器は団員との絆！\nこれからもあなたのこと\n頼りにさせてもらうからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255011, /*unit_id*/125501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/10.1, /*change_face*/5, /*description*/"実は、ケーキを用意したんだ！\n魔法を使うのはズルい気がして\n手作りに挑戦したんだけど…\n気に入ってくれるといいな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1255012, /*unit_id*/125531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"団員の誕生日だもん\n思いっきり祝わなくっちゃ！\n見てて、エーテルブラストを\n花火代わりに打ち上げるから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256001, /*unit_id*/125601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"遠路はるばるランドソルに来て\n正解だったよ！\nまさにラッキー！　クッキー！\nビッキィ！　だね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256002, /*unit_id*/125601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.5, /*change_face*/1, /*description*/"ランドソルの街並はどこも\n見事だね！　しっかりと目に\n焼き付けてパレードの山車の\n参考にさせてもらうよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256003, /*unit_id*/125601, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.1, /*change_face*/2, /*description*/"この街で一番活気があるのは\nどこかな？　ネズ耳を売るなら\nそういうところが\nうってつけだからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256004, /*unit_id*/125601, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"やぁ、いらっしゃい！　ネズ耳を\n売るのを手伝ってくれるかい？\nきみと一緒なら\nたくさん売れる気がするんだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256005, /*unit_id*/125601, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"きみの運勢は大吉だよ！\nなんたって鼠神宮の巫女が\nそばにいるんだからね！\nこれって最高のご利益だろう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256006, /*unit_id*/125631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんな集まれー！\nぼくと一緒に楽しく踊ろうよ！\nイヤなことも辛いことも全部\nぼくが忘れさせてあげるからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256007, /*unit_id*/125631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぼくが笑顔でいれば\nつられてみんなも笑顔になる\nだからぼくはいつも\n笑っていたいんだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256008, /*unit_id*/125631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな人でも手を繋げば\nもう友達さ！　街中の人と\n仲良くなってみせるよ！\nさぁ、ランドソルでぼくと握手！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256009, /*unit_id*/125631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きみが笑顔でいると、不思議と\nぼくまで幸せな気持ちになるんだ\nだからきみのこと\nずっと笑わせてあげるからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256010, /*unit_id*/125631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もし元気が出ない時があれば\n遠慮なくぼくを呼んでほしいな！\nどれだけ離れていたとしても\n駆けつけて笑顔にしてみせるよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256011, /*unit_id*/125601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.2, /*change_face*/2, /*description*/"きみ、今日誕生日なんだって？\nそういうことなら早速お祝いだ！\nハッピー！　バースデー！\nトゥーユー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1256012, /*unit_id*/125631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！　今日一日\nきみのお祝いをしてあげるよ！\nビッキィを独り占めできるなんて\nきみは幸せ者だね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1257001, /*unit_id*/125701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.6, /*change_face*/2, /*description*/"錬金術は、戦闘のサポートに\n長けた学問ですね\n私の性格に合っていますし\n冒険の手助けは任せてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1257002, /*unit_id*/125701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.6, /*change_face*/2, /*description*/"実際に冒険に出ることで\nサポート業務に何が必要かを\nしっかり学ばせていただきます\nさあ、冒険にでかけましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1257003, /*unit_id*/125701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.1, /*change_face*/2, /*description*/"現場でのサポートだけでなく…\nもちろん、ギルド管理協会として\n事前のサポートも行いますよ\n冒険のお手伝いは任せてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1257004, /*unit_id*/125701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/"あなたと一緒なら\nどんな危険な場所でも\n不安はありません\n背中はお任せしますね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1257005, /*unit_id*/125701, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/8.2, /*change_face*/2, /*description*/"じー…はっ、すみません！\n次はいつ冒険に行かれるのか\n気になってしまい、つい…\n私はいつでも大丈夫です！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1257006, /*unit_id*/125731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、調合の時間です！\n素敵なお薬でみなさんを\nサポートいたしますね\n私にお任せください♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
