INSERT INTO `unit_comments` VALUES (/*id*/1257010, /*unit_id*/125731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の錬金術が、あなたの冒険に\n少しでも役に立つように…\nそう思って心を込めて作成中です\nこれが私なりの戦い方ですから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
